INSERT INTO `avatar_trigger_texts` (`id`, `trigger_step_id`, `language`, `text`) VALUES
	(1, 1, 'en', '[informal_name] has sent you a request'),
	(2, 1, 'nl', '[informal_name] heeft u een verzoek gestuurd'),
	(3, 1, 'es', '[informal_name] te ha enviado una solicitud para ser tu cuidador'),
	(4, 2, 'en', '[senior_name] has sent you a request'),
	(5, 2, 'nl', '[informal_name] heeft u een verzoek gestuurd'),
	(6, 2, 'es', '[senior_name] te ha enviado una solicitud para que seas su cuidador'),
	(7, 3, 'en', '[informal_name] has added the following comment to your goal [goal_name]:“ [comment_text]”'),
	(8, 3, 'nl', '[informal_name] heeft dit bericht onder uw doel [goal_name] geplaatst: “ [comment_text]”'),
	(9, 3, 'es', '[informal_name] ha añadido el siguiente comentario a tu objectivo [goal_name]: “[comment_text]”'),
	(10, 4, 'en', '[informal_name] has added the following picture to your goal [goal_name]: [image_path]'),
	(11, 4, 'nl', '[informal_name] heeft deze afbeelding onder jouw doel [goal_name] geplaatst: [image_path]'),
	(12, 4, 'es', '[informal_name] ha añadido la siguiente imagen a tu objetivo [goal_name]: [image_path]'),
	(13, 5, 'en', '[informal_name] wants to encourage you to achieve your goal [goal_name]'),
	(14, 5, 'nl', '[informal_name] moedigt je aan om je doel [goal_name] te behalen'),
	(15, 5, 'es', '[informal_name], ¡Ánimo con ese objetivo de [goal_name]!'),
	(16, 6, 'en', '[informal_name] likes your goal [goal_name]'),
	(17, 6, 'nl', '[informal_name] vindt uw doel [goal_name] leuk'),
	(18, 6, 'es', 'A [informal_name] le gusta tu objetivo [goal_name]'),
	(19, 7, 'en', '“Hi [senior_name], it’s been a month since you started tracking your health and wellbeing. Shall we have a look at your progress?'),
	(20, 7, 'nl', 'Hallo [senior_name], het is een maand geleden dat u bent begonnen met het bijhouden van uw gezondheid. Zullen we uw voortgang bekijken?'),
	(21, 7, 'es', 'Hola [senior_name], ha pasado un mes desde que empezaste a llevar un seguimiento de tu salud y bienestar. ¿Quieres echar un vistazo al progreso?'),
	(22, 8, 'en', 'Hi [senior_name], I have noticed you have taken off your smartwatch. If you want to keep track of your progress, it would be great if you can put it back on!'),
	(23, 8, 'nl', 'Hallo [senior_name], ik zie dat je je smartwatch hebt afgedaan. Als je jouw voortgang wil blijven bijhouden, is het handig om hem weer te gaan dragen.'),
	(24, 8, 'es', '[senior_name], me he dado cuenta que no llevas puesto el reloj. Vamos, póntelo y sé consciente de tus progresos.'),
	(25, 9, 'en', 'I am happy to see that you are wearing your fitbit again, great job!'),
	(26, 9, 'nl', 'Ik ben blij te zien dat je je smartwatch weer draagt, goed gedaan!'),
	(27, 9, 'es', '¡Qué bien que te hayas puesto de nuevo el dispositivo! ¡Buen trabajo!'),
	(28, 10, 'en', 'Good evening [senior_name], it’s almost bed time. It would be great for you to wear your smartwatch while you sleep tonight. This way you can see how much you''ve rested.'),
	(29, 10, 'nl', 'Goedenavond [senior_name], het is bijna bedtijd. Het zou goed zijn om uw smartwatch vannacht ook om te houden, zodat u kunt bijhouden hoe goed uw slaap was.'),
	(30, 10, 'es', 'Buenas noches [senior_name], es casi la hora de ir a dormir. No olvides seguir usando tu dispositivo mientras duermes para poder ver lo bien que has dormido.'),
	(31, 11, 'en', 'Good morning [senior_name] I hope you had a good night and are ready for today. Please don’t forget to wear your smartwatch, so you can see your progress.'),
	(32, 11, 'nl', 'Goedemorgen, [senior_name] ik hoop dat u goed heeft geslapen en klaar bent voor de dag. Vergeet niet om uw smartwatch weer om te doen, zodat u uw voortgang bij kunt houden.'),
	(33, 11, 'es', 'Buenos días [senior_name], espero que hayas dormido bien. Oye, no olvides ponerte tu reloj para que podamos ver cómo progresas.'),
	(34, 12, 'en', 'Good morning [senior_name], I hope you’ve had a good night and are ready for a wonderful day. Would you mind telling me how you are feeling today?'),
	(35, 12, 'nl', 'Hallo [senior_name], ik hoop dat u een goede nacht heeft gehad. Zou u mij iets willen vertellen over uw huidige stemming?'),
	(36, 12, 'es', 'Buenos días [senior_name], espero que hayas tenido una buena noche y cogido fuerzas para afrontar este maravilloso día. ¿Cómo te sientes hoy?'),
	(37, 12, 'hu', 'Jó reggelt, [senior_name], remélem, hogy kellemesen telt az éjszakája és készen áll egy csodás napra! Feltehetek Önnek néhány kérdést a hangulatával kapcsolatosan?'),
	(38, 13, 'en', 'Hello [senior_name], don’t forget to charge your smartwatch, the batteries are empty.'),
	(39, 13, 'nl', 'Hallo [senior_name], vergeet niet om je smartwatch op te laden, de batterij is namelijk bijna leeg.'),
	(40, 13, 'es', 'Hola [senior_name], ¡no olvides cargar tu dispositivo! Parece que la batería está vacía'),
	(41, 14, 'en', 'Hello [senior_name], the batteries of your smartwatch are almost empty. Please take it off to charge it.'),
	(42, 14, 'nl', 'Hallo [senior_name], de batterij van je smartwatch is bijna leeg. Doe de smartwatch af om hem op te laden.'),
	(43, 14, 'es', 'Hola [senior_name], tu dispositivo apenas tiene batería. Ponlo a cargar durante una hora'),
	(44, 15, 'en', 'Your smartwatch is charged, you can put it back on to track your progress.'),
	(45, 15, 'nl', 'Je smartwatch is opgeladen, je kan hem weer omdoen om je voortgang bij te houden.'),
	(46, 15, 'es', 'Tu dispositivo ya debe estar cargado. Puedes ponértelo de nuevo'),
	(47, 16, 'en', 'Hello [informal_name], [senior_name] has performed a new self-report. The analysis of results says: [recommendation_text]'),
	(48, 16, 'nl', 'Hallo [informal_name], [senior_name] heeft een nieuwe vragenlijst ingevuld. De resultaten van de analyse zeggen het volgende [recommendation_text]'),
	(49, 16, 'es', 'Hola [informal_name], [senior_name] ha completado un cuestionario. El análisis de los resultados indica que: [recommendation_text]'),
	(50, 17, 'en', 'Hello [Informal_name], [senior_name] has reported that he is feeling [senior_mood]'),
	(51, 17, 'nl', 'Hallo [informal_name], heeft aangegeven dat hij zich [senior_mood] voelt'),
	(52, 17, 'es', 'Hola [informal_name], [senior_name] ha indicado que se siente [senior_mood]'),
	(53, 18, 'en', '[name] has uploaded content: [content_name]'),
	(54, 18, 'nl', '[name] heeft content geupload: [content_name]'),
	(55, 18, 'es', '[name] ha subido contenido: [content_name]'),
	(56, 19, 'en', '[name] has started a new discussion: [discussion_name]'),
	(57, 19, 'nl', '[name] heeft een nieuwe discussie gestart: [discussion_name]'),
	(58, 19, 'es', '[name] ha iniciado un debate: [discussion_name]'),
	(59, 20, 'en', '[formal_name] noticed some changes in the wellbeing of [person_name], [formal_name] suggests that you make an appointment with a health professional.'),
	(60, 20, 'nl', '[formal_name] heeft veranderingen in de gezondheid van [senior_name] waargenomen, [formal_name] stelt voor om een afspraak bij de huisarts te maken.'),
	(61, 20, 'es', '[formal_name] ha descubierto algunos parámetros que podrían afectar el bienestar de [senior_name]. [formal_name] considera que sería bueno que concretases cita con un profesional que chequease su estado de salud.'),
	(62, 21, 'en', 'We have noticed some changes in the wellbeing of the [senior_name], it would be good to contact a health professional to check if [senior_name] is feeling well.'),
	(63, 21, 'nl', 'We hebben veranderingen in de gezondheid van [senior_name] waargenomen, het zou verstandig zijn een afspraak te maken bij de huisarts om te checken of [senior_name] zich in orde is.'),
	(64, 21, 'es', 'Hemos descubierto algunos parámetros que podrían afectar el bienestar de [senior_name]. Sería bueno que concretases cita con un profesional que chequease que [senior_name] se siente bien.'),
	(65, 22, 'en', '[name] are now part of MyGuardian'),
	(66, 22, 'nl', '[name] zijn nu onderdeel van MyGuardian'),
	(67, 22, 'es', '[name], son ahora parte de MyGuardian'),
	(68, 23, 'en', 'Sorry, something went wrong. Please try again.'),
	(69, 23, 'nl', 'Excuus, iets ging niet helemaal goed. Probeer het nogmaals.'),
	(70, 23, 'es', 'Perdona, algo fue mal. Por favor, inténtalo de nuevo'),
	(71, 24, 'en', 'Sorry, something went wrong. Please try again.'),
	(72, 24, 'nl', 'Excuus, iets ging niet helemaal goed. Probeer het nogmaals.'),
	(73, 24, 'es', 'Perdona, algo fue mal. Por favor, inténtalo de nuevo'),
	(74, 25, 'en', '[informal_name] from MyGuardian has been added to your list of caregivers in CoME'),
	(75, 25, 'nl', '[informal_name] van MyGuardian is toegevoegd aan je lijst van zorgverleners in CoME'),
	(76, 25, 'es', '[informal_name] ha sido añadido a tu lista de cuidadores en CoME desde MyGuardian'),
	(77, 26, 'en', '[senior_name] from MyGuardian has been added to your list of seniors in CoME'),
	(78, 26, 'nl', '[senior_name] van MyGuardian is toegevoegd aan je lijst van senioren in CoME'),
	(79, 26, 'es', '[senior_name] ha sido añadido a tu lista de mayores en CoME'),
	(80, 27, 'en', 'Great [senior_name], I’m glad you’re feeling excited today. Let’s tell your loved-ones; I’m sure this will make them happy too!'),
	(81, 27, 'nl', 'Geweldig [senior_name], Ik ben blij dat u zich opgewekt voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat zij daar ook blij van worden!'),
	(82, 27, 'es', 'Bien, [senior_name]. ¡Qué alegría que te encuentres entusiasmado! Déjame que se lo cuente a tus cuidadores: ¡seguro que ellos también se alegran!'),
	(83, 27, 'hu', 'Nagyszerű, [senior_name], örömmel értesülök róla, hogy várakozással tölti el a mai nap. Ossza meg ezt a jó hírt szeretteivel is, biztos, hogy az ő napjukat is megszépíti ezzel!'),
	(84, 28, 'en', 'Too bad [senior_name], I’m sorry you’re feeling tense today. Let’s tell your loved-ones; Perhaps they can help you relax!'),
	(85, 28, 'nl', 'Vervelend [senior_name], het spijt me dat u zich gespannen voelt vandaag. Laten we het aan uw naasten laten weten. Misschien kunnen zij u helpen met ontspannen!'),
	(86, 28, 'es', '¡Qué pena, [senior_name]! ¡Cuánto siento que estés bajo tensión! Déjame que se lo comente a tus cuidadores. Seguro que ellos pueden ayudar a relajarte.'),
	(87, 28, 'hu', 'Sajnálom, [senior_name], remélem, hogy hamar elmúlik ez a feszültség. Mondja el a szeretteinek, hogy mi a probléma, hátha tudnak segíteni abban, hogy megnyugodjon!'),
	(88, 29, 'en', 'Oh no [senior_name], I’m sorry you’re feeling irritated today. Let’s tell your loved-ones; Maybe it will help to talk about it!'),
	(89, 29, 'nl', 'Wat vervelend [senior_name], het spijt me dat u zich gespannen voelt vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u helpen te ontspannen!'),
	(90, 29, 'es', '¡Ay, qué pena, [senior_name]! Siento que estés enfadado. Déjame comentárse lo a tus cuidadores más cercanos. Quizás ellos puedan ayudar a que te encuentres mejor.'),
	(91, 29, 'hu', 'Sajnálom, [senior_name], remélem, hogy hamar elmúlik ez a feszültség. Mondja el a szeretteinek, hogy mi a probléma, hátha tudnak segíteni abban, hogy megnyugodjon!'),
	(92, 30, 'en', 'That’s a pity [senior_name], I’m sorry you’re feeling sad today. Let’s tell your loved-ones; Perhaps they can help cheer you up!'),
	(93, 30, 'nl', 'Jammer [senior_name], het spijt me dat u zich verdrietig voelt vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u helpen om u zich beter te laten voelen!'),
	(94, 30, 'es', '¡Qué lástima, [senior_name]! Siento que estés triste. Déjame comentárselo a tus cuidadores. Quizás puedan ayudarte a sentirte mejor.'),
	(95, 30, 'hu', 'Kár, [senior_name], hogy ma szomorú, de a szerettei talán tudnának segíteni ez ügyben! Mondja el nekik, hogy mit érez!'),
	(96, 31, 'en', 'Bummer [senior_name], I’m sorry you’re feeling bored today. Let’s tell your loved-ones; Perhaps they can cheer you up!'),
	(97, 31, 'nl', 'Jammer [senior_name], het spijt me dat u zich verveeld vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u opvrolijken!'),
	(98, 31, 'es', 'Lástima, [senior_name]. Siento que estés aburrido. Déjame comentárselo a tus cuidadores. Quizás ellos encuentren algo que te entretenga.'),
	(99, 31, 'hu', 'Sajnálom, [senior_name], hogy egykedvűnek érzi ma magát! Mondja el ezt szeretteinek, hátha fel tudják dobni Önt!'),
	(100, 32, 'en', 'Nice [senior_name], I’m glad you’re feeling calm today. Let’s tell your loved-ones; they will be happy to hear you are doing well.'),
	(101, 32, 'nl', 'Fijn [senior_name], ik ben blij dat u zich rustig voelt vandaag. Laten we het ook aan uw naasten vertellen. Zij zullen ook blij zijn om te horen dat het goed met u gaat.'),
	(102, 32, 'es', '¡Genial [senior_name]! Cuánto me alegro de que estés calmado. Vamos a decírselo a tus seres queridos. Les encantará saber lo bien que te sientes'),
	(103, 32, 'hu', 'Kiváló [senior_name], örülök, hogy nyugodtanak érzi magát ma. Ossza meg ezt szeretteivel, biztosan örülnének, ha tudnák, hogy Önnel minden rendben.'),
	(104, 33, 'en', 'Wonderful [senior_name], I’m glad you’re feeling relaxed today. Let’s tell your loved-ones; I’m sure this will make them feel good about today too!'),
	(105, 33, 'nl', 'Goed gedaan [senior_name], ik ben blij dat u zich ontspannen voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat zij zich daardoor ook vandaag ook goed zullen voelen!'),
	(106, 33, 'es', 'Bien hecho, [senior_name]. ¡Qué alegría que estés relado! Déjame comentárselo a tus cuidadores, seguro que esto también les relaja a ellos.'),
	(107, 33, 'hu', 'Kiváló, [senior_name], örülök, hogy ma kipihent! Értesítse erről szeretteit is, biztos, hogy ez jó érzéssel tölti majd el őket mára!'),
	(108, 34, 'en', 'Great [senior_name], I’m glad you’re feeling cheerful today. Let’s tell your loved-ones; I’m sure this will put a smile on their faces too!'),
	(109, 34, 'nl', 'Goed werk [senior_name], ik ben blij dat u zich vrolijk voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat het ook een lach op hun gezicht zal toveren!'),
	(110, 34, 'es', 'Genial, [senior_name]. ¡Qué contento me hace que estés tan alegre hoy! Déjame comentárselo a tus cuidadores. Esto seguro que querrán saber por qué estás tan contento.'),
	(111, 34, 'hu', 'Remek hírek, [senior_name], örülök, hogy vidáman indul a napja! Ossza meg örömét szeretteivel, biztos, hogy ez az ő arcukra is mosolyt csal!'),
	(112, 35, 'en', 'Good morning [senior_name], I hope you had a good night and are ready for a wonderful day. Would you mind answering some questions about your mood now? Ok/not now'),
	(113, 35, 'nl', 'Hallo [senior_name], ik hoop dat je een goede nacht hebt gehad. Zou je me nu iets willen vertellen over jouw stemming? Ok/niet nu'),
	(114, 35, 'es', 'Buenos días [senior_name], espero que pasases buena noche y estés preparado para este maravilloso día. ¿Te importaría responder algunas preguntas sobre tu estado de ánimo? Ok/Ahora no'),
	(115, 35, 'hu', 'Jó reggelt, [senior_name], remélem, hogy kellemesen telt az éjszakája és készen áll egy csodás napra! Feltehek Önnek néhány kérdést a hangulatával kapcsolatosan? Rendben/ most nem'),
	(116, 36, 'en', 'Hello [senior_name], would you like to tell me how you’re doing now? Ok/not now'),
	(117, 36, 'nl', 'Hallo [senior_name], wil je me nu vertellen hoe het met je gaat? Ok/niet nu'),
	(118, 36, 'es', '¡Hola [senior_name]! Dime cómo te sientes hoy. Ok/Ahora no'),
	(119, 36, 'hu', 'Kedves [senior_name], megosztaná velem, hogy hogyan érzi most magát? Igen/ most nem'),
	(120, 37, 'en', 'Hi [senior_name], you don''t have any health goals yet. It would be great if you would start by setting a new goal for yourself. Would you like me to help you with that? Ok/ not now'),
	(121, 37, 'nl', 'Hallo [senior_name], je hebt nog geen gezondheidsdoelen gesteld. Het zou geweldig zijn als je zou beginnen met een nieuw doel. Wil je dat ik je daarbij help? Ok/ niet nu'),
	(122, 37, 'es', '¡Hola [senior_name]! Aún no has establecido ningún objetivo. Sería bueno que comenzases a plantearte nuevos objetivos. ¿Deseas que te ayude a establecerte objetivos? Sí/ Ahora no'),
	(123, 37, 'hu', 'Kedves [senior_name], még nem adott meg egy célt sem, ami segíthetne egészségesebbé válnia. Nagyszerű kezdés lenne, ha beállítana egyet! Segíthetek Önnek ebben? Igen/ most nem'),
	(124, 38, 'en', 'Nice, [senior_name]! I’m excited about your new walking goal! I’m sure you will do great. Remember to add this goal to your daily timeline.'),
	(125, 38, 'nl', 'Goed zo, [senior_name]! Ik ben enthousiast over jouw nieuwe doel om te wandelen! Ik weet zeker dat je het fantastisch gaat doen! Vergeet niet om dit doel toe te voegen aan jouw tijdslijn.'),
	(126, 38, 'es', '¡Bien hecho, [senior_name]! Me encanta tu objetivo de caminar. Seguro que lo haces genial. No olvides añadir este objetivo a tu calendario.'),
	(127, 38, 'hu', 'Kiváló, [senior_name]! Jó választás az új célja a lépésszám terén, biztos vagyok benne, hogy remekül teljesít majd! Ne felejtse el ezt a célt hozzáadni az idővonalához a mai napra!'),
	(128, 39, 'en', 'Perfect [senior_name], It’s fine to feel neutral sometimes. Let’s tell your loved-ones; I’m sure they’ll be interested in how you’re doing!'),
	(129, 39, 'nl', 'Perfect [senior_name], het is prima om je soms neutraal te voelen. Laten we het aan jouw naasten vertellen; Ik weet zeker dat ze geïnteresseerd zullen zijn in hoe jij je vandaag voelt!'),
	(130, 39, 'es', 'Perfecto [senior_name]. No pasa nada por sentirse imparcial. Déjame comentárselo a tus cuidadores, seguro que están interesados en saber cómo lo haces.'),
	(131, 39, 'hu', 'Kiváló, [senior_name], teljesen természetes, ha az ember közömbösen érzi magát néha. Mondja el ezt szeretteinek, biztos örülnének, ha tudnák, hogy indul az Ön napja!'),
	(132, 1, 'hu', '[informal_name] kérelmet küldött Önnek'),
	(133, 2, 'hu', '[senior_name] kérelmet küldött Önnek'),
	(134, 3, 'hu', '[informal_name]a következő hozzászólást tette közzé az Ön célját illetően [goal_name]:” [comment_text”'),
	(135, 4, 'hu', '[informal_name] a következő képet adta hozzá az Ön céljához [goal_name]:[image_path]'),
	(136, 5, 'hu', '[informal_name]biztatni szeretné Önt, hogy elérje a célját [goal_name]'),
	(137, 6, 'hu', '[informal_name] kedveli az Ön célját [goal_name]'),
	(138, 7, 'hu', 'Kedves [senior_name] egy hónapja már, hogy elkezdte nyomon követni az egészségi állapotát. Kívánja megtekinteni, hogy ez idő alatt milyen eredményeket ért el?'),
	(139, 8, 'hu', 'Kedves [senior_name] Észleltem, hogy Ön levette az okosóráját. Hogyha továbbra is szeretné nyomon követni az állapotát, annak érdekében kérem vegye fel újra az órát!'),
	(140, 9, 'hu', 'Örömmel látom, hogy újra viseli az órát,, ez nagyszerű!'),
	(141, 10, 'hu', 'Jó estét [seniro_name], majdnem itt a lefekvés ideje, remek lenne, ha ma éjjel viselné az okosóráját, így láthatná , hogy hogy aludt az éjjel az óra méréseiből.'),
	(142, 11, 'hu', 'Jó reggelt [senior_name] Remélem jó éjszakája volt, és készen áll a mai napra! Kérem, ne felejtse el viselni az okosóráját, így láthatja állapota alakulását.'),
	(143, 13, 'hu', 'Üdvözlöm [senior_name], ne felejtse el feltölteni az okosoráját, az akkumulátor lemerült.'),
	(144, 14, 'hu', 'Üdvözlöm [senior_name], az okosórájának az akkumulátora majdnem lemerült. Kérem, tegye fel töltőre.'),
	(145, 15, 'hu', 'Az okosórája feltöltött, most már újra felveheti, hogy továbbra is nyomon tudja követni állapotát.'),
	(146, 16, 'hu', 'Üdvözöljük [informal_name]! [senior_name] újabb beszámolót töltött ki állapota felmérésére. Az eredmény a válaszai alapján a következő:\n[recommendation_text]'),
	(147, 17, 'hu', 'Üdvözlöm [informal_name], [senior_name] közzé tette, hogy hogy érzi magát: [senior_mood]'),
	(148, 18, 'hu', '[name] feltöltötte mennyire elégedett: [content_name]'),
	(149, 19, 'hu', '[name] új beszélgetést indított: [discussion_name]'),
	(150, 20, 'hu', '[formal_name] észrevett néhány változást [senior_name], [formal_name] egészségügyi állapota kapcsán. Azt szeretné Önnek ajánlani, hogy beszéljen meg egy időpontot egészségügyi szakemberrel.'),
	(151, 21, 'hu', 'Észrevettünk [senior_name] egészségügyi állapotában néhány változást. Jó ötlet lenne, ha felvenné a kapcsolatot egy egészségügyi szakemberrel, hogy megvizsgálja [senior_name]-t, hogy jól érzi e magát.'),
	(152, 22, 'hu', '[name] mostantól tagja a MyGuardian-nak.'),
	(153, 23, 'hu', 'Elnézést, valami nem működik megfelelően jelenleg. Kérem próbálja meg újra.'),
	(154, 24, 'hu', 'Elnézést, valami nem működik megfelelően jelenleg. Kérem próbálja meg újra.'),
	(155, 25, 'hu', '[informal_name] a MyGuardianról hozzá lett adva a CoME-n szereplő gondviselők listájához.'),
	(156, 26, 'hu', '[senior_name] a MyGuardianról hozzá lett adva a CoME-n szereplő idősek listájához.'),
	(157, 64, 'en', '<h2>Hello [person_name], welcome to CoMe!</h2>\n<p>\nMy name is Eva and I’m here to help you find your way in CoMe. I can also help you when you’re stuck and will occasionally check up on you to see how you’re doing.\n</p><p>\nYou can find me on the right of every page.\n</p><p>\nNow let’s get you started!\n</p>'),
	(158, 64, 'es', '<h2>¡Hola [person_name]! Bienvenido a CoME!</h2>\n<p>\nMi nombre es Eva y estoy aquí para iniciarte en el uso de la plataforma CoME. Además, estaré siempre pendiente de ti, evaluando cómo te sientes o recordándote cosillas.\n</p><p>\nPuedes encontrarme en la parte derecha de cada página.\n</p><p>\n¡Vamos a empezar!\n</p>'),
	(159, 64, 'nl', '<h2>Hallo [person_name], welkom bij CoME!</h2>\n<p>\nMijn naam is Eva en ik wil u graag helpen om uw weg te vinden binnen CoME. Ook kan ik u helpen wanneer u vastloopt en zal ik soms even vragen hoe het met u gaat.\nU kunt mij hier vinden wanneer u mij nodig heeft.\n</p><p>\nLaten we gauw gaan beginnen!\n</p>'),
	(160, 64, 'hu', '<h2>Üdvözlöm a CoME oldalán kedves [person_name]!</h2>\n<p>\nAz én nevem Éva és szeretnék Önnek segíteni eligazodnia a CoME oldalán. Akkor is az Ön segítségére fogok sietni, ha valahol elakad és alkalmanként jelentkezni fogok automatikusan.\n</p><p>\nBármerre is jár a CoME oldalán, segítségért forduljon hozzám, a jobb szélen találhat meg.\n</p><p>\nÉs akkor kezdjük is az ismerkedést az oldalal!\n</p>'),
	(161, 65, 'en', '<h2>Setting up your goals</h2>\n<p>\nGreat [person_name]!\n</p><p>\nAfter this introduction you can start by adding a new goal. The goals will help you to keep on going, and stay healthy.\n</p><p>\nGoals can be as small or big as you like, but let’s start with something small like ‘walk 30 minutes a day.’ You can create your own goal or select one of the predefined goals.\n</p><p>\nTo create a new goal please go to your Goals and click on “Add New Goal.”\n</p><p>\nStep 1/8\n</p>'),
	(162, 65, 'es', '<h2>Establecer tus objetivos</h2>\n<p>\n¡Genial [person_name]!\n</p><p>\nDespués de esta introducción, puedes empezar estableciendo un objetivo. Los objetivos te ayudan a mantenerte activo y estar sano.\n</p><p>\nLos objetivos pueden ser tan sencillos o complejos como quieras, aunque es mejor empezar con algo sencillo como “caminar 30 min al día”. Puedes crear tu propio objetivo o seleccionar uno de los objetivos predefinidos.\n</p><p>\nPara crear un nuevo objetivo, ve a Objetivos y cliquea “Añadir Nuevo Objetivo”.\n</p><p>\nPaso 1/8\n</p>'),
	(163, 65, 'nl', '<h2>Doelen opstellen</h2>\n<p>\nGeweldig [person_name]!\n</p><p>\nNa deze introductie kunt u beginnen met een nieuw doel op te stellen. Doelen helpen u om niet op te geven en zorgen ervoor dat u gezond blijft.\n</p><p>\nDoelen kunnen zo groot of klein zijn als u zelf wilt. Zullen we eerst beginnen met iets kleins, zoals ‘30 minuten per dag wandelen.’? U kunt uw eigen doelen stellen of een vooraf ingevuld doel kiezen.\n</p><p>\nGa naar uw doelen om een nieuw doel aan te maken en kies “Nieuw doel toevoegen.”\n</p><p>\nStap 1/8\n</p>'),
	(164, 65, 'hu', '<h2>Céljai beállítása</h2>\n<p>\nKedves [person_name]!\n</p><p>\nEzután a rövid bemutató után kezdésképp érdemes volna beállítania egy célt. A megadott célok segíteni fogják Önt egészsége megőrzésében és abban, hogy motivált maradjon mindvégig.\nEzeknek a céloknak a volumene csak Öntől függ, de kezdjünk csak egy egyszerűbbel, pl. napi 30 perc séta. Saját célt is alkothat vagy választhat az oldal által előre megadottakból is.\n</p><p>\nÚj cél hozzáadásához kérjük menjen a Célok menüpontra, majd azon belül kattintson az “Alapvető vagy egyéni cél hozzáadása” gombra.\n</p><p>\nLépés 1/8\n</p>'),
	(165, 66, 'en', '<h2>View your progress</h2>\n<p>\nDo you want to know how you are doing? You can get insight in your lifestyle by looking at your trends.\nBy connecting your device you can get insights in your physical activities, food intake, heart rate and sleeping patterns. By doing so you’ll be able to reach your goals more easily.\nHow great is that?\n</p><p>\nStep 2/8\n</p>'),
	(166, 66, 'es', '<h2>Seguir tu progreso</h2>\n<p>\n¿Quieres saber cómo te encuentras? Puedes obtener información sobre tu estilo de vida y progreso en la sección Evolución.\nConectando tu disposivito puedes tener una mejor perspectiva de tu actividad física, consumo alimenticio, frecuencia cardíaca y patrones de sueño.\nHaciendo esto, serás capaz de alcanzar tus objetivos de forma más sencilla. ¿ No te parece fantástico?\n</p><p>\nPaso 2/8\n</p>'),
	(167, 66, 'nl', '<h2>Uw voortgang bekijken</h2>\n<p>\nWilt u weten hoe goed u het doet? U kunt inzicht krijgen in uw levensstijl door naar uw trends te kijken.\n</p><p>\nDoor uw activity tracker aan te sluiten kunt u inzicht krijgen in uw fysieke activiteit, dieet, hartslag en slaappatronen.\n</p><p>\nDoor dit te doen zult u gemakkelijker uw doelen bereiken. Klinkt dat niet geweldig?\n</p><p>\nStap 2/8\n</p>'),
	(168, 66, 'hu', '<h2>Nézze meg, hogyan teljesített</h2>\n<p>\nKíváncsi rá, hogy milyen fejlődésen ment keresztül? A “Tendenciák” menüpontba kattintva megtekintheti az egészségével kapcsolatos mérhető adatait.\nAzzal, hogy mérőeszközét (okosóra, okosmérleg, vérnyomásmérő, stb.) összeköti a CoME oldalával hozzáférhet fizikai aktivitásával kapcsolatos adataihoz, bejegyzett étkezési szokásaihoz, pulzusadataihoz, illetve alvási szokásaihoz.\nEnnek a folyamatnak köszönhetően könnyebben valósíthatja meg elérendő céljait. Ez egy nagyszerű lehetőség, nemde?!\n</p><p>\nLépés 2/8\n</p>'),
	(169, 67, 'en', '<h2>Connecting your device</h2>\n<p>\nAre you ready to connect your device? In the top right corner click on the user icon to connect your device. Click on “wearable devices” open the page and to choose the right device. Are you ready? Then click on “connect your device”.\n</p><p>\nStep 3/8\n</p>'),
	(170, 67, 'es', '<h2>Conectar tu dispositivo</h2>\n<p>\n¿Estás listo para conectar tu dispositivo? En la esquina de arriba a la derecha, pincha en el icono de usuario para conectar tu dispositivo. Pincha en “Dispositivos” para abrir la página adecuada y el dispositivo correcto. ¿Estás listo? Ahora pincha en “conectar dispositivo”.\n</p><p>\nPaso 3/8\n</p>'),
	(171, 67, 'nl', '<h2>Uw activity tracker aansluiten</h2>\n<p>\nBent u er klaar voor om uw activity tracker aan te sluiten? Rechtsboven in uw scherm kunt u klikken op het gebruikers icoontje. Click daarna op “activity trackers”\n</p><p>\nEr opent een pagina, kies de juiste activity tracker. Bent u er klaar voor? Klik dan op “sluit mijn activity tracker aan”\n</p><p>\nStap 3/8\n</p>'),
	(172, 67, 'hu', '<h2>Mérőeszköze csatlakoztatása</h2>\n<p>\nKészen áll arra, hogy csatlakoztassa mérőeszközét?\nAmennyiben igen, a jobb felső sarokban levő embert formáló ikonra kattintva találhatja a “Mérőeszközök” menüpontot. Erre kattintva válassza ki az Ön által csatlakoztatni kívánt készüléket. Ha ez megvan, akkor kattintson a “Kapcsolat létrehozása a mérőeszközével” gombra.\n</p><p>\nLépés 3/8\n</p>'),
	(173, 68, 'en', '<h2>Sharing your feeling</h2>\n<p>\nYou’re doing wonderful so far!\n</p><p>\nYou can fill-out a report about yourself that can help you to get better insight in your lifestyle and it allows you to assess yourself so you can make changes when needed.\n</p><p>\nThe information from the self-report and also from your device will provide input for the trend analysis.\n</p><p>\nStep 4/8\n</p>'),
	(174, 68, 'es', '<h2>Compartir cómo te sientes</h2>\n<p>\n¡Lo estás haciendo genial!\n</p><p>\nPuedes completar un informe sobre ti que te ayude a tener una mejor perspectiva de tu estilo de vida y decirte qué se puede mejorar en tu objetivo saludable.\n</p><p>\nLa información de estos test de auto-evaluación junto con tu dispositivo, te ayudarán a medir tu progreso.\n</p><p>\nPaso 4/8\n</p>'),
	(175, 68, 'nl', '<h2>Deel uw gevoelens</h2>\n<p>\nTot nu toe doet u het geweldig!\n</p><p>\nU kunt een vragenlijst invullen over uzelf om beter inzicht te krijgen in uw levensstijl en het creëert inzicht in hoe u zelf veranderingen kunt maken in uw leven.\n</p><p>\nDe informatie van de vragenlijst en van uw activity tracker zal genoeg zijn voor het creëren van een trendanalyse.\n</p><p>\nStap 4/8\n</p>'),
	(176, 68, 'hu', '<h2>Hangulata megosztása</h2>\n<p>\nEddig jól halad.\n</p><p>\nA CoME lehetőséget nyújt Önnek arra, hogy időről-időre rövid kérdésekre válaszolva jegyezhesse fel Ön az aktuális hangulatát.\n</p><p>\nEzeket az adatokat összevetve az aznapi, mérőeszköze által mért aktivitási/egészségügyi adatokkal képet kaphat arról, hogy min lenne érdemes változtatnia egészsége és boldogsága érdekében.\n</p><p>\nLépés 4/8\n</>'),
	(177, 69, 'en', '<h2>Personalized reports from professionals</h2>\n<p>\nThe care professionals will support you in achieving a healthier lifestyle. You can check their latest updates in “Reports”.\n</p><p>\nStep 5/8\n</p>'),
	(178, 69, 'es', '<h2>Informes personalizados de un profesional</h2>\n<p>\nUn equipo de profesionales te ayudarán a seguir un estilo de vida más saludable. Puedes revisar sus últimas recomendaciones en “Informes”\n</p><p>\nPaso 5/8\n</p>'),
	(179, 69, 'nl', '<h2>Rapportages van professionals</h2>\n<p>\nDe zorgprofessionals zullen u ondersteunen om een gezondere levensstijl te bereieken. U kunt hun laatste updates zien onder “Rapportages”.\n</p><p>\nStap 5/8\n</p>'),
	(180, 69, 'hu', '<h2>Személyre szabott jelentések a CoME orvosi csapatától</h2>\n<p>\nÖnt a CoME oldal használata során egészségügyi dolgozók fogják segíteni egészsége megőrzésében. Az “Információ” menüpontra kattintva találhatja az általuk elkészített jelentéseket.\n</p><p>\nLépés 5/8\n</p>'),
	(181, 70, 'en', '<h2>Receiving support from your network</h2>\n<p>\nHere you can find your support network. You can add friends or family members that can support you in achieving your healthier lifestyle.\n</p><p>\nStep 6/8\n</p>'),
	(182, 70, 'es', '<h2>Recibir apoyo de tus seres queridos</h2>\n<p>\nAquí puedes saber qué personas cercanas contribuyen a ayudarte en tu estilo de vida saludable.\nPuedes añadir tanto amigos como familiares para que te ayuden en este estilo de vida saludable.\n</p><p>\nPaso 6/8\n</p>'),
	(183, 70, 'nl', '<h2>Beginnen met CoME</h2>\n<p>\nNaasten\n</p><p>\nHier kunt u de ondersteuning binnen uw netwerk vinden. U kunt vrienden of familie toevoegen die u kunnen ondersteunen in het bereiken van een gezondere levensstijl.\n</p><p>\nStep 6/8\n</p>'),
	(184, 70, 'hu', '<h2>Az Ön támogatói hálózata</h2>\n<p>\nAz “Ismerősök” menüpont alatt találhatja az Ön családtagjainak, barátainak körét, akik regisztráltak a CoME odalra és Ön felvette velük a kapcsolatot az oldalon keresztül, hogy támogassák egészsége megóvásában.\n</p><p>\nLépés 6/8\n</p>'),
	(185, 71, 'en', '<h2>Collaborating with your network</h2>\n<p>\nWell done.\n</p><p>\nYou can use MyGuardian to get in touch with the people around you.\n</p><p>\nMy Guardian allows you to get in touch with your loved ones. Feeling under the weather? They can give you a helping hand when needed. Or you can share the latest updates using “messages”.\n</p><p>\nLet''s have a look at My Guardian!\n</p><p>\nStep 7/8\n</p>'),
	(186, 71, 'es', '<h2>Colaboración con tu red</h2>\n<p>\nBien hecho.\n</p><p>\nPuedes usar MyGuardian para contactar con las personas de tu entorno.\n</p><p>\nMyGuardian te permite estar en contacto con tus seres queridos.\n</p><p>\n¿Te sientes mal? Ellos siempre pueden prestarte una mano. O puedes compartir las últimas actualizaciones usando “mensajes”.\n</p><p>\n¡Vamo a echar un vistazo a MyGuardian!\n</p><p>\nPaso 7/8\n</p>'),
	(187, 71, 'nl', '<h2>Met uw netwerk samenwerken</h2>\n<p>\nGoed gedaan.\n</p><p>\nU kunt gebruik maken van MyGuardian en in contact te komen met de mensen die dichtbij u staan.\n</p><p>\nMyGuardian zorgt ervoor dat u in contact komt met uw naasten. Voelt u zich niet zo lekker? Zij kunnen u een handje helpen wanneer dat nodig is. Of u kunt hen een update geven door de “berichten” functie te gebruiken.\n</p><p>\nLaten we eens naar MyGuardian gaan kijken!\n</p><p>\nStap 7/8\n</p>'),
	(188, 71, 'hu', '<h2>Együttműködés az Ön hálózatával</h2>\n<p>\nNagyszerű.\n</p><p>\nA MyGuardian programját arra használhatja főként, hogy tartsa a kapcsolatot az Ön környezetében levő emberekkel.\n</p><p>\nBiztosítja, hogy szeretteivel könnyen tudjon kommunikálni. Vegyünk egy példát. Megfázott? Tudassa velük (és csak velük) és ők segíthetnek Önnek. Vagy megoszthatja a belső körével a legújabb történéseket az “üzenetek” menüpontban.\n</p><p>\nNézzük csak meg, hogy is működik a MyGuardian!\n</p><p>\nLépés 7/8\n</p>'),
	(189, 72, 'en', '<h2>Almost done!</h2>\n<p>\nFantastic [person_name]!\n</p><p>\nYou’re ready to explore what CoMe has to offer on your own. If you want to change your settings you can go to your profile page.\n</p><p>\nThank you for your attention and I will see you soon!\n</p><p>\n*tip: Wish to relax? CoMe also offers various games!\n</p><p>\nStep 8/8\n</p>'),
	(190, 72, 'es', '<h2>¡Ya casi está!</h2>\n<p>\n¡Fantástico [person_name]!\n</p><p>\nEstás preparado para echar un vistazo por ti mismo a lo que CoME te ofrece. Si quieres cambiar los ajustes, puedes ir a la página de perfil.\n</p><p>\n¡Gracias por tu atención! ¡Espero verte pronto!\n</p><p>\n*consejo: ¿Te quieres relajar?\n</p><p>\n¡CoME también te ofrece juegos!\n</p><p>\nPaso 8/8\n</p>'),
	(191, 72, 'nl', '<h2>Bijna klaar!</h2>\n<p>\nFantastisch [person_name]!\n</p><p>\nU bent er klaar voor om CoMe alleen verder te ontdekken. Als u aanpassingen wilt maken in uw instellingen kunt u dit doen door naar uw profielpagina te gaan.\n</p><p>\nDank u wel voor uw aandacht en ik zie u snel weer terug!\n</p><p>\n* tip: Wilt u even ontspannen? CoMe biedt ook verschillende spelletjes aan!\n</p><p>\nStap 8/8\n</p>'),
	(192, 72, 'hu', '<h2>Majdnem készen vagyunk!</h2>\n<p>\nMár könnyebben lesz képes ezentúl felfedezni a CoME oldalán, hogy az mit is nyújthat Önnek. Ha szeretné megváltoztatni a beállításait, akkor lépjen fel a profiloldalára.\n</p><p>\nKöszönöm az eddigi figyelmét, hamarosan találkozunk!\n</p><p>\nTipp: Ha szeretne kikapcsolódni, a CoME játékokat is biztosít Önnek, melyeket a jobb alsó sarokban találhat.\n</p><p>\nLépés 8/8\n</p>'),
	(193, 73, 'en', 'Dear [person_name]\nI have just switched my language to match your preferences!'),
	(194, 73, 'es', '[person_name], idioma cambiado'),
	(195, 73, 'nl', 'Beste [person_name], mijn taal is aangepast aan uw voorkeuren.'),
	(196, 73, 'hu', 'Kedves [person_name], innentől az Ön által preferált nyelven láthatja az üzeneteimet!'),
	(197, 74, 'en', 'A new report with recommendations from professionals has just been sent to you! Do you want to look at it now?'),
	(198, 75, 'en', 'The professional supervising you has noticed some changes in your wellbeing. Maybe it would be good to contact your health professional.'),
	(199, 76, 'en', '“Hello [senior_name], we have new questionnaires available for you to fill in. Do you want to fill in a new questionnaire to evaluate your progress?'),
	(200, 77, 'en', 'Hi [person_name]! I am not noticing any activity from you. Are you wearing your smartwatch?\n\nI am unable to monitor your wellbeing. Please, could you put the smartwatch back on for me?”'),
	(201, 78, 'en', 'Hi [person_name], it’s been a great week. You have really helped me keep track your health and wellbeing. Thank you! Shall we have a look at all the things that happened?'),
	(202, 79, 'en', 'Hi [person_name], we noticed you have been less active in the last couple of days. It would be great if we could turn this around. Would you like me to help you with that?”'),
	(203, 80, 'en', 'Hi [person_name], you’ve really been doing well lately, you have been quite active. Actually, you seem to be quite a champion!'),
	(204, 81, 'en', 'Hi [person_name],\nI see you haven’t filled in any goals. Goals will help you in achieving a healthier life style.\n\nDo you want me to help you set up a new goal? I know you will do amazing!'),
	(205, 82, 'en', 'Congratulations, [person_name]! You’ve achieved the [goal] goal. That’s amazing! Are you ready for a next challenge?'),
	(206, 83, 'en', 'Good evening [person_name]. You’ve had a great day today, as you achieved your [goal]. I think your loved-ones would be very happy to see your achievement. Would you like to share it with them?'),
	(207, 84, 'en', 'Good morning, [person_name]! I’m excited you’ll be starting your new goal [goal]. Perhaps your loved-ones will feel inspired by you. Would you like to share your new goal with your network?'),
	(208, 85, 'en', 'Hi [senior name], I have noticed you have not yet created a goal. It would be great to see how you set a new goal.'),
	(209, 86, 'en', 'Hi [senior name], you have not yet connected any devices. Do you need help to connect your device?'),
	(210, 87, 'en', 'Hi [senior name], on this page you can view your progress. Click on any theme you are interested in to see more details.'),
	(211, 88, 'en', '[senior_name] has received a new report from a professional. Take a look!'),
	(212, 89, 'en', 'Hello [person_name], [senior_name] has reported that he/she has reached his/her [goal]. How about showing them how proud you are!'),
	(213, 90, 'en', 'Hi [informal_name], it’s been a great month.\n<p>\nWe have really helped keep track of [senior_name] health and wellbeing.\n</p><p>\nThank you! Shall we have a look at all the things that happened?\n<p>'),
	(214, 91, 'en', 'Hello [informal_name], [senior_name] has create a new goal [goal]. How about showing them how proud you are!'),
	(215, 92, 'en', 'Hi [informal_name]. [senior name] has told he/she is bored today.\n<p>\nHow about sending him/her a suggestion on how to turn their day around.\n</p><p>\nOr even better how about visiting them and doing a nice activity.\n</p>'),
	(216, 93, 'en', 'Good morning [senior_name], I hope you had a good night and are ready for a wonderful day. Do you want to tell me about your mood today? Or how are you felling today?'),
	(217, 94, 'en', 'Hello [senior_name], would you like to tell me how you’re doing now?'),
	(218, 95, 'en', 'Thanks for sharing how you feel. Let’s tell your loved-ones; I’m sure they’ll be interested to know how you’re doing!'),
	(228, 74, 'es', 'Te hemos enviado un nuevo informe con recomendaciones de un profesional. ¿Quieres mirarlo ahora?'),
	(229, 75, 'es', 'El profesional que revisa tu progreso ha detectado algunos cambios en tu estado de salud. Quizás podrías contactar con un profesional de la salud.'),
	(230, 76, 'es', 'Hola [senior_name], ¿por qué no completas uno de los cuestionarios disponibles para evaluar tu progreso?'),
	(231, 77, 'es', '¡Hola [person_name]! Hace tiempo que no hay actividad. ¿Llevas puesto tu dispositivo?\n\nNo soy capaz de monitorizar tu estado de salud. Por favor, ¿ podrías ponerte de nuevo el reloj por mí?'),
	(232, 78, 'es', 'Hola [person_name], ha sido una gran semana. Me has ayudado mucho a chequear tu estado de salud y bienestar. ¡Gracias! ¿Quieres echar un vistazo a todo lo que has logrado?'),
	(233, 79, 'es', 'Hola [person_name], hemos notado que has estado poco activo en los últimos días. Sería bueno que te reactivases. ¿Quisieras ayudarme con esto?'),
	(234, 80, 'es', 'Hola [person_name], lo has estado haciendo muy bien últimamente. ¡Eres todo un ganador!'),
	(235, 81, 'es', 'Hola [person_name], he visto que todavía no has añadido un objetivo. Los objetivos te ayudan a conseguir un estilo de vida más saludable.\n\n¿Quieres ayudarme y establecer un nuevo objetivo? ¡Seguro que lo haces genial!'),
	(236, 82, 'es', '¡Enhorabuena [person_name]! Has logrado tu objetivo [goal]. ¿Estás listo para el siguiente desafío?'),
	(237, 83, 'es', 'Buenas tardes [person_name]. Has tenido un día genial, a medida que alcanzabas tu objetivo [objetivo].\nCreo que tus seres queridos estarán muy felices de ver tus logros. ¿Te gustaría compartirlo con ellos?'),
	(238, 84, 'es', '¡Buenos días, [person_name]! Estoy feliz de que hayas creado tu nuevo objetivo [goal]. Quizás tus seres queridos se animen a conseguirlo también. ¿Quieres comentar este nuevo objetivo con tus seres queridos?'),
	(239, 85, 'es', 'Hola [senior_name], he notado que todavía no has creado un objetivo. Estaría genial ver cómo estableces tus objetivos.'),
	(240, 86, 'es', 'Hola [senior_name], todavá no has conectado ningún dispositivo. ¿Necesitas ayuda para conectar tu dispositivo?'),
	(241, 87, 'es', 'Hola [senior_name], en esta página puedes ver tu progreso. Pincha en cualquier tema en el que estés interesado para ver más detalles.'),
	(242, 88, 'es', '[senior_name] ha recibido un nuevo informe de un profesional. ¡Echa un vistazo!'),
	(243, 89, 'es', 'Hola [person_name], [senior_name] ha indicado que ha alcanzado su objetivo [objetivo]. ¡Muéstrales lo orgulloso que te sientes!'),
	(244, 90, 'es', 'Hola [informal_name], ha sido un mes genial.\n<p>\nNos has ayudado un montón a seguir al usuario [senior_name].\n</p><p>\n¡Gracias! ¿Quieres echar un vistazo a su perfil?\n</p>'),
	(245, 91, 'es', 'Hola [informal_name], [senior_name] ha creado un nuevo objetivo [objetivo]. ¡Muéstrale lo orgulloso que te sientes!'),
	(246, 92, 'es', 'Hola [informal_name]. [senior_name] dice que está un poco aburrido.\n<p>\n¿Por qué no le envías una sugerencia para mejorar su día?\n</p><p>\nO mejor aún, ¿por qué no la visitas y hacéis algo juntas?\n</p>'),
	(247, 93, 'es', 'Buenos días , [senior_name]. Espero que hayas pasado una buena noche y que tengas un día genial. Dime cómo te sientes hoy.'),
	(248, 94, 'es', '¿Qué tal [senior_name]? ¿Cómo te encuentras hoy?'),
	(249, 95, 'es', 'Gracias por compartir como te sientes. Vamos a decírselo a tus seres queridos. ¡Estoy segura de que querrán saberlo!'),
	(259, 74, 'nl', 'Een nieuw verslag met adviezen van professionals is naar u verstuurd! Wilt u er nu naar kijken?'),
	(260, 75, 'nl', 'Uw professional heeft veranderingen in uw gezondheid opgemerkt. Misschien is het een goed idee om contact op te nemen met uw professional.'),
	(261, 76, 'nl', 'Hallo [senior_name], wij hebben nieuwe vragenlijsten voor u om in te vullen. Wilt u een nieuwe vragenlijst invullen om uw voortgang vast te leggen?'),
	(262, 77, 'nl', 'Hallo [person_name]! Het valt mij op dat ik geen activiteiten van u binnenkrijg. Draagt u uw activity tracker?'),
	(263, 78, 'nl', 'Hallo [person_name], deze week was echt geweldig. U heeft mij echt geholpen met het bijhouden van uw gezondheid. Dank u wel! Zullen we samen kijken naar hoe uw week is verlopen?'),
	(264, 79, 'nl', 'Hallo [person_name], het is ons opgevallen dat u minder actief bent geweest de laatste paar dagen. Het zou super zijn als we hier iets aan kunnen doen. Wilt u dat ik u hiebrij help?'),
	(265, 80, 'nl', 'Hallo [ person_name], u heeft het super goed gedaan de laatste tijd, u bent erg actief geweest. Of eigenlijk, u bent gewoon een kampioen!'),
	(266, 81, 'nl', 'Hallo [person_name], Ik zie dat u nog geen doelen heeft ingevuld. Doelen helpen u een gezondere levensstijl te bereiken.\n\nWilt u dat ik u help met het opzetten van een nieuw doel? Ik weet dat u het geweldig zult doen!'),
	(267, 82, 'nl', 'Gefeliciteerd, [person_name]! U heeft het [goal] doel bereikt! Dat is echt geweldig! Bent u klaar voor de volgende uitdaging?'),
	(268, 83, 'nl', 'Goedenavond [person_name]. U heeft vandaag een goede dag gehad, u heeft namelijk uw [goal] doel bereikt. Ik denk dat uw naasten het leuk zullen vinden om te zien dat u uw doel heeft bereikt. Zou u dit met hen willen delen?'),
	(269, 84, 'nl', 'Goedemorgen [person_name]! Ik ben verheugd over dat u vandaag begint met [goal] doel. Misschien zullen uw naasten door u geïnspireerd worden. Wilt u uw nieuwe doel met hen delen?'),
	(270, 85, 'nl', 'Hallo [senior_name], het is mij opgevallen dat u nog geen doel heeft ingesteld. Het zou fijn zijn als u een nieuw doel op zou stellen.'),
	(271, 86, 'nl', 'Hallo [senior_name], u heeft vandaag geen verbinding gemaakt met uw activity tracker. Heeft u hulp nodig hulp nodig bij het instellen hiervan?'),
	(272, 87, 'nl', 'Hallo [senior_name], op deze pagina vindt u uw voortgang. Klik op een onderdeel die u interesseert om er meer informatie over te zien.'),
	(273, 88, 'nl', '[senior_name] heeft een nieuw verslag van een professional ontvangen! Neem een kijkje!'),
	(274, 89, 'nl', 'Hallo [person_name], [senior_name] heeft aangeven dat hij/zij haar/zijn [goal] doel heeft bereikt. Laat u even weten hoe trots u bent?'),
	(275, 90, 'nl', 'Hallo [informal_name], het is een geweldige maand geweest.\n<p>\nWe hebben echt geholpen met het bijhouden van de gezondheid van [senior_name].\n</p><p>\nDank u wel voor uw inzet! Zullen we eens kijken naar alles wat is gebeurd afgelopen maand?\n</p>'),
	(276, 91, 'nl', 'Hallo [informal_name], [senior_name] heeft een nieuw doel ingesteld! [goal]. Laat u even weten hoe trots u bent?'),
	(277, 92, 'nl', 'Hallo [informal_name]. [senior_name] heeft aangegeven dat hij/zij zich vandaag verveeld.\n<p>\nMisschien kunt u hem/haar een suggestie geven over hoe zij/hij hun dag kan veranderen.\n</p><p>\nOf nog beter: u kunt hem/haar bezoeken en samen iets leuks doen.\n</p>'),
	(278, 93, 'nl', 'Goedemorgen [senior_name], ik hoop dat u lekker heeft geslapen en klaar bent voor een geweldige dag. Wilt u mij vertellen over uw stemming vandaag? Of over hoe u zich vandaag voelt?'),
	(279, 94, 'nl', 'Hallo [senior_name], wilt u mij vertellen over hoe het met u gaat?'),
	(280, 95, 'nl', 'Dank u wel dat u met mij deelt hoe het met u gaat. Laten we het ook aan uw naasten vertellen. Ik weet zeker dat ze daarin geïnteresseerd zijn!'),
	(290, 74, 'hu', 'A CoME orvosi csapatának tagjai épp most állítottak össze Önnek egy jelentést állapotáról ajánlásokkal. Szeretné megtekinteni most?'),
	(291, 75, 'hu', 'A tanácsadója változást észlelt az Ön egészségi állapotában. Érdemes lehet felvennie a kapcsolatot orvosával/ápolójával.'),
	(292, 76, 'hu', 'Üdvözüljük [senior_name]! Újabb kérdőívek állnak rendelkezésre, hogy kitöltse őket. Kívánja ezt most megtenni, hogy segítségükkel felmérje állapotát?'),
	(293, 77, 'hu', 'Helló [person_name]! Egy ideje nem érzékeltem aktivitást az Ön részéről. Viseli az okosóráját?\n\nSajnos képtelen vagyok így felügyelni az egészségi állapotát. Kérem, kezdje el újra hordani az óráját, hogy segíthessek, rendben?'),
	(294, 78, 'hu', 'Helló [person_name], nagyszerű hetet tudhat maga mögött. Sokat segített abban, hogy megfelelően nyomon követhessem egészségi állapotát, köszönöm! Szeretné megtekinteni, hogy milyen eredményeket ért el?'),
	(295, 79, 'hu', 'Helló [person_name], látszólag csökkent az aktivitási szintje az elmúlt pár napban. Jó lenne, ha tudna ezen változtatni, nemde? Segíthetek Önnek ebben?'),
	(296, 80, 'hu', 'Helló [person_name], az elmúlt időszakban meglehetősen aktív volt, jól haladt céljai felé. Így tovább, jó úton jár!'),
	(297, 81, 'hu', 'Helló [person_name], úgy látom, hogy nem adott meg elérendő célokat, pedig sokat segíthetnek abban, hogy egészségesebbé tudjon válni.\n\nSegíthetek Önnek, hogy beállítsunk együtt egy új célt? Biztos vagyok benne, hogy meg tudja valósítani!'),
	(298, 82, 'hu', 'Gratulálok, [person_name]! Elérte a kitűzött célját, kiváló! Készen áll a következő kihívásra?'),
	(299, 83, 'hu', 'Jó estét [person_name]!\nRemek napot tudhat maga mögött, elérte a célját: [goal].\nA szerettei biztos örömmel látnák ezt. Megosztja velük eredményét?'),
	(300, 84, 'hu', 'Jó reggelt [person_name]!\nIzgatottan várom, hogy elkezdje megvalósítani új célját: [goal]. Célja megosztásával a szeretteit is motiválhatja! Kívánja megosztani velük új célját?'),
	(301, 85, 'hu', 'Helló [senior_name], úgy látom, még nem jelölt ki célt magának. Sokat segíthetne egészsége megőrzésében, ha ezt megtenné.'),
	(302, 86, 'hu', 'Helló [senior_name], még nem csatlakoztatott fiókjához egyetlen mérőeszközt sem. Szüksége van segítségre, hogy ezt megtegye?'),
	(303, 87, 'hu', 'Helló [senior_name], ezen az oldalon tájékozódhat arról, miként alakultak egészségügyi paraméterei. Válassza ki bármelyik szegmenst, ami érdekli, hogy adatait részletesebben láthassa.'),
	(304, 88, 'hu', '[senior_name] számára épp most küldött egy kiértékelést tanácsadója. Tekintse meg Ön is!'),
	(305, 89, 'hu', 'Helló [person_name], [senior_name] épp most valósította meg egy kitűzött célját. Tudassa vele, hogy büszke rá elért eredményéért!'),
	(306, 90, 'hu', 'Helló [informal_name], remek hónapon vagyunk túl.\n<p>\nSokat segítettünk [senior_name] egészségi állapotának nyomon követésében. Köszönet az Ön hozzájárulásáért! Szeretné megtekinteni, hogy mi történt ebben a periódusban?\n</p>'),
	(307, 91, 'hu', 'Helló [informal_name], [senior_name] épp most tűzött ki egy új célt magának. Tudassa vele, hogy büszke rá motiváltságáért!'),
	(308, 92, 'hu', 'Helló [informal_name], [senior_name] azt mondja, hogy ma unalmas napja van.\n<p>\nÉrdemes lenne egy javaslattal előállnia számára üzenetben, hogy hogyan dobhatja fel a napját.\n</p><p>\nHa személyesen is meg tudná látogatni, hogy közös programot csináljanak, az még jobb lenne!\n</p>'),
	(309, 93, 'hu', 'Jó reggelt [senior_name]! Remélem jól aludt éjszaka és készen áll egy csodás napra. Megosztaná velem, hogy milyen hangulatban van ma és hogy érzi magát?'),
	(310, 94, 'hu', 'Helló [senior_name], megkérdezhetem, hogy hogy telik eddig a napja?'),
	(311, 95, 'hu', 'Köszönöm, hogy megosztotta velem, hogy milyen hangulatban van. Ossza meg szeretteivel is ezt, biztos vagyok benne, hogy érdekli őket az Ön hogyléte!'),
	(325, 96, 'en', 'Hello [senior_name], can I ask you how you are doing? Your answer will help us in the long run as we will be able to compare the interaction between your physical activity, diet, sleeping pattern and mental state.'),
	(326, 97, 'en', 'The results of the questionnaire say [recommendation_text]”'),
	(328, 96, 'es', 'Hola [senior_name], ¿cómo estás? ¿Te gustaría completar un cuestionario para evaluar tu progreso en actividad física, dieta, sueño, estado mental o estado de ánimo?'),
	(329, 97, 'es', 'Los resultados del cuestionario dicen [recommendation_text]'),
	(331, 96, 'nl', 'Hallo [senior_name], mag ik u vragen hoe het met u gaat? Uw antwoord zal ons inzicht geven in de wisselwerkingen tussen uw fysieke activiteit, dieet, slaappatroon en gemoedstoestand op de lange termijn.'),
	(332, 97, 'nl', 'De resultaten van de vragenlijst zeggen [recommendation_text].'),
	(334, 96, 'hu', 'Hello [senior_name], Feltehetek Önnek egy kérdést a mai hangulatával kapcsolatosan? Fontos lenne válaszolnia annak érdekében, hogy láthassa, mennyire függ össze fizikai aktivitása, étrendje, alvási szokásai és mentális állapota a pillanatnyi hangulatával hosszú távon!'),
	(335, 97, 'hu', 'A kérdőív eredménye alapján: [recommendation_text]');
INSERT INTO `avatar_trigger_text_create_import` (`id`, `trigger_id`, `serial_number`, `en`, `es`, `nl`, `hu`) VALUES
	(9, 18, 1, '“Hello [senior_name], how are you doing, would you like to fill-out a questionnaire to evaluate your progress?', 'Hola [senior_name], ¿cómo estás? ¿Te gustaría completar un cuestionario para evaluar tu progreso?', 'Hallo [senior_name], hoe gaat het met u? Zou u een vragenlijst willen invullen om uw voortgang te kunnen vastleggen?', 'Hello [senior_name], hogyan csinálod, szeretnél kitölteni egy kérdőívet, hogy értékelje a fejlődésedet?'),
	(10, 19, 1, '“The results of the questionnaire say [recommendation_text]”', '“Los resultados del cuestionario dicen [recommendation_text]', 'De resultaten van de vragenlijst bevelen het volgende aan: [recommendation_text]', 'A kérdőív eredményei szerint [recommendation_text]');
INSERT INTO `avatar_trigger_text_update_import` (`id`, `trigger_id`, `serial_number`, `en`, `es`, `nl`, `hu`) VALUES
	(1, 1, 1, '[informal_name] has sent you a request', '[informal_name] te ha enviado una solicitud para ser tu cuidador', '[informal_name] heeft u een verzoek gestuurd', '[informal_name] kérelmet küldött Önnek'),
	(2, 2, 1, '[senior_name] has sent you a request', '[senior_name] te ha enviado una solicitud para que seas su cuidador', '[informal_name] heeft u een verzoek gestuurd', '[senior_name] kérelmet küldött Önnek'),
	(3, 3, 1, '[informal_name] has added the following comment to your goal [goal_name]:“ [comment_text]”', '[informal_name] ha añadido el siguiente comentario a tu objectivo [goal_name]: “[comment_text]”', '[informal_name] heeft dit bericht onder uw doel [goal_name] geplaatst: “ [comment_text]”', '[informal_name]a következő hozzászólást tette közzé az Ön célját illetően [goal_name]:” [comment_text”'),
	(4, 6, 1, '[informal_name] likes your goal [goal_name]', 'A [informal_name] le gusta tu objetivo [goal_name]', '[informal_name] vindt uw doel [goal_name] leuk', '[informal_name] kedveli az Ön célját [goal_name]'),
	(5, 7, 1, '“Hi [senior_name], it’s been a month since you started tracking your health and wellbeing. Shall we have a look at your progress?', 'Hola [senior_name], ha pasado un mes desde que empezaste a llevar un seguimiento de tu salud y bienestar. ¿Quieres echar un vistazo al progreso?', 'Hallo [senior_name], het is een maand geleden dat u bent begonnen met het bijhouden van uw gezondheid. Zullen we uw voortgang bekijken?', 'Kedves [senior_name] egy hónapja már, hogy elkezdte nyomon követni az egészségi állapotát. Kívánja megtekinteni, hogy ez idő alatt milyen eredményeket ért el?'),
	(6, 10, 1, 'Good evening [senior_name], it’s almost bed time. It would be great for you to wear your smartwatch while you sleep tonight. This way you can see how much you''ve rested.', 'Buenas noches [senior_name], es casi la hora de ir a dormir. No olvides seguir usando tu dispositivo mientras duermes para poder ver lo bien que has dormido.', 'Goedenavond [senior_name], het is bijna bedtijd. Het zou goed zijn om uw smartwatch vannacht ook om te houden, zodat u kunt bijhouden hoe goed uw slaap was.', 'Jó estét [seniro_name], majdnem itt a lefekvés ideje, remek lenne, ha ma éjjel viselné az okosóráját, így láthatná , hogy hogy aludt az éjjel az óra méréseiből.'),
	(7, 11, 1, 'Good morning [senior_name] I hope you had a good night and are ready for today. Please don’t forget to wear your smartwatch, so you can see your progress.', 'Buenos días [senior_name], espero que hayas dormido bien. Oye, no olvides ponerte tu reloj para que podamos ver cómo progresas.', 'Goedemorgen, [senior_name] ik hoop dat u goed heeft geslapen en klaar bent voor de dag. Vergeet niet om uw smartwatch weer om te doen, zodat u uw voortgang bij kunt houden.', 'Jó reggelt [senior_name] Remélem jó éjszakája volt, és készen áll a mai napra! Kérem, ne felejtse el viselni az okosóráját, így láthatja állapota alakulását.'),
	(8, 12, 1, 'Good morning [senior_name], I hope you’ve had a good night and are ready for a wonderful day. Would you mind telling me how you are feeling today?', 'Buenos días [senior_name], espero que hayas tenido una buena noche y cogido fuerzas para afrontar este maravilloso día. ¿Cómo te sientes hoy?', 'Hallo [senior_name], ik hoop dat u een goede nacht heeft gehad. Zou u mij iets willen vertellen over uw huidige stemming?', 'Jó reggelt, [senior_name], remélem, hogy kellemesen telt az éjszakája és készen áll egy csodás napra! Feltehetek Önnek néhány kérdést a hangulatával kapcsolatosan?'),
	(9, 18, 1, 'Hello [senior_name], can I ask you how you are doing? Your answer will help us in the long run as we will be able to compare the interaction between your physical activity, diet, sleeping pattern and mental state.', 'Hola [senior_name], ¿cómo estás? ¿Te gustaría completar un cuestionario para evaluar tu progreso en actividad física, dieta, sueño, estado mental o estado de ánimo?', 'Hallo [senior_name], mag ik u vragen hoe het met u gaat? Uw antwoord zal ons inzicht geven in de wisselwerkingen tussen uw fysieke activiteit, dieet, slaappatroon en gemoedstoestand op de lange termijn.', 'Hello [senior_name], Feltehetek Önnek egy kérdést a mai hangulatával kapcsolatosan? Fontos lenne válaszolnia annak érdekében, hogy láthassa, mennyire függ össze fizikai aktivitása, étrendje, alvási szokásai és mentális állapota a pillanatnyi hangulatával hosszú távon!'),
	(10, 59, 1, '“Hello [senior_name], we have new questionnaires available for you to fill in. Do you want to fill in a new questionnaire to evaluate your progress?', 'Hola [senior_name], ¿por qué no completas uno de los cuestionarios disponibles para evaluar tu progreso?', 'Hallo [senior_name], wij hebben nieuwe vragenlijsten voor u om in te vullen. Wilt u een nieuwe vragenlijst invullen om uw voortgang vast te leggen?', 'Üdvözüljük [senior_name]! Újabb kérdőívek állnak rendelkezésre, hogy kitöltse őket. Kívánja ezt most megtenni, hogy segítségükkel felmérje állapotát?'),
	(11, 19, 1, 'The results of the questionnaire say [recommendation_text]”', 'Los resultados del cuestionario dicen [recommendation_text]', 'De resultaten van de vragenlijst zeggen [recommendation_text].', 'A kérdőív eredménye alapján: [recommendation_text]'),
	(12, 23, 1, 'Hello [informal_name], [senior_name] has performed a new self-report. The analysis of results says: [recommendation_text]', 'Hola [informal_name], [senior_name] ha completado un cuestionario. El análisis de los resultados indica que: [recommendation_text]', 'Hallo [informal_name], [senior_name] heeft een nieuwe vragenlijst ingevuld. De resultaten van de analyse zeggen het volgende [recommendation_text]', 'Üdvözöljük [informal_name]! [senior_name] újabb beszámolót töltött ki állapota felmérésére. Az eredmény a válaszai alapján a következő:\n[recommendation_text]'),
	(13, 24, 1, 'Hello [Informal_name], [senior_name] has reported that he is feeling [senior_mood]', 'Hola [informal_name], [senior_name] ha indicado que se siente [senior_mood]', 'Hallo [informal_name], heeft aangegeven dat hij zich [senior_mood] voelt', 'Üdvözlöm [informal_name], [senior_name] közzé tette, hogy hogy érzi magát: [senior_mood]'),
	(14, 27, 1, '[formal_name] noticed some changes in the wellbeing of [person_name], [formal_name] suggests that you make an appointment with a health professional.', '[formal_name] ha descubierto algunos parámetros que podrían afectar el bienestar de [senior_name]. [formal_name] considera que sería bueno que concretases cita con un profesional que chequease su estado de salud.', '[formal_name] heeft veranderingen in de gezondheid van [senior_name] waargenomen, [formal_name] stelt voor om een afspraak bij de huisarts te maken.', '[formal_name] észrevett néhány változást [senior_name], [formal_name] egészségügyi állapota kapcsán. Azt szeretné Önnek ajánlani, hogy beszéljen meg egy időpontot egészségügyi szakemberrel.'),
	(15, 34, 1, 'Great [senior_name], I’m glad you’re feeling excited today. Let’s tell your loved-ones; I’m sure this will make them happy too!', 'Bien, [senior_name]. ¡Qué alegría que te encuentres entusiasmado! Déjame que se lo cuente a tus cuidadores: ¡seguro que ellos también se alegran!', 'Geweldig [senior_name], Ik ben blij dat u zich opgewekt voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat zij daar ook blij van worden!', 'Nagyszerű, [senior_name], örömmel értesülök róla, hogy várakozással tölti el a mai nap. Ossza meg ezt a jó hírt szeretteivel is, biztos, hogy az ő napjukat is megszépíti ezzel!'),
	(16, 35, 1, 'Too bad [senior_name], I’m sorry you’re feeling tense today. Let’s tell your loved-ones; Perhaps they can help you relax!', '¡Qué pena, [senior_name]! ¡Cuánto siento que estés bajo tensión! Déjame que se lo comente a tus cuidadores. Seguro que ellos pueden ayudar a relajarte.', 'Vervelend [senior_name], het spijt me dat u zich gespannen voelt vandaag. Laten we het aan uw naasten laten weten. Misschien kunnen zij u helpen met ontspannen!', 'Sajnálom, [senior_name], remélem, hogy hamar elmúlik ez a feszültség. Mondja el a szeretteinek, hogy mi a probléma, hátha tudnak segíteni abban, hogy megnyugodjon!'),
	(17, 36, 1, 'Oh no [senior_name], I’m sorry you’re feeling irritated today. Let’s tell your loved-ones; Maybe it will help to talk about it!', '¡Ay, qué pena, [senior_name]! Siento que estés enfadado. Déjame comentárse lo a tus cuidadores más cercanos. Quizás ellos puedan ayudar a que te encuentres mejor.', 'Wat vervelend [senior_name], het spijt me dat u zich gespannen voelt vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u helpen te ontspannen!', 'Sajnálom, [senior_name], remélem, hogy hamar elmúlik ez a feszültség. Mondja el a szeretteinek, hogy mi a probléma, hátha tudnak segíteni abban, hogy megnyugodjon!'),
	(18, 37, 1, 'That’s a pity [senior_name], I’m sorry you’re feeling sad today. Let’s tell your loved-ones; Perhaps they can help cheer you up!', '¡Qué lástima, [senior_name]! Siento que estés triste. Déjame comentárselo a tus cuidadores. Quizás puedan ayudarte a sentirte mejor.', 'Jammer [senior_name], het spijt me dat u zich verdrietig voelt vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u helpen om u zich beter te laten voelen!', 'Kár, [senior_name], hogy ma szomorú, de a szerettei talán tudnának segíteni ez ügyben! Mondja el nekik, hogy mit érez!'),
	(19, 38, 1, 'Bummer [senior_name], I’m sorry you’re feeling bored today. Let’s tell your loved-ones; Perhaps they can cheer you up!', 'Lástima, [senior_name]. Siento que estés aburrido. Déjame comentárselo a tus cuidadores. Quizás ellos encuentren algo que te entretenga.', 'Jammer [senior_name], het spijt me dat u zich verveeld vandaag. Laten we het aan uw naasten vertellen; Misschien kunnen zij u opvrolijken!', 'Sajnálom, [senior_name], hogy egykedvűnek érzi ma magát! Mondja el ezt szeretteinek, hátha fel tudják dobni Önt!'),
	(20, 39, 1, 'Nice [senior_name], I’m glad you’re feeling calm today. Let’s tell your loved-ones; they will be happy to hear you are doing well.', '¡Genial [senior_name]! Cuánto me alegro de que estés calmado. Vamos a decírselo a tus seres queridos. Les encantará saber lo bien que te sientes', 'Fijn [senior_name], ik ben blij dat u zich rustig voelt vandaag. Laten we het ook aan uw naasten vertellen. Zij zullen ook blij zijn om te horen dat het goed met u gaat.', 'Kiváló [senior_name], örülök, hogy nyugodtanak érzi magát ma. Ossza meg ezt szeretteivel, biztosan örülnének, ha tudnák, hogy Önnel minden rendben.'),
	(21, 40, 1, 'Wonderful [senior_name], I’m glad you’re feeling relaxed today. Let’s tell your loved-ones; I’m sure this will make them feel good about today too!', 'Bien hecho, [senior_name]. ¡Qué alegría que estés relado! Déjame comentárselo a tus cuidadores, seguro que esto también les relaja a ellos.', 'Goed gedaan [senior_name], ik ben blij dat u zich ontspannen voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat zij zich daardoor ook vandaag ook goed zullen voelen!', 'Kiváló, [senior_name], örülök, hogy ma kipihent! Értesítse erről szeretteit is, biztos, hogy ez jó érzéssel tölti majd el őket mára!'),
	(22, 41, 1, 'Great [senior_name], I’m glad you’re feeling cheerful today. Let’s tell your loved-ones; I’m sure this will put a smile on their faces too!', 'Genial, [senior_name]. ¡Qué contento me hace que estés tan alegre hoy! Déjame comentárselo a tus cuidadores. Esto seguro que querrán saber por qué estás tan contento.', 'Goed werk [senior_name], ik ben blij dat u zich vrolijk voelt vandaag. Laten we het aan uw naasten vertellen; Ik weet zeker dat het ook een lach op hun gezicht zal toveren!', 'Remek hírek, [senior_name], örülök, hogy vidáman indul a napja! Ossza meg örömét szeretteivel, biztos, hogy ez az ő arcukra is mosolyt csal!'),
	(23, 50, 1, '<h2>Hello [person_name], welcome to CoMe!</h2>\n<p>\nMy name is Eva and I’m here to help you find your way in CoMe. I can also help you when you’re stuck and will occasionally check up on you to see how you’re doing.\n</p><p>\nYou can find me on the right of every page.\n</p><p>\nNow let’s get you started!\n</p>', '<h2>¡Hola [person_name]! Bienvenido a CoME!</h2>\n<p>\nMi nombre es Eva y estoy aquí para iniciarte en el uso de la plataforma CoME. Además, estaré siempre pendiente de ti, evaluando cómo te sientes o recordándote cosillas.\n</p><p>\nPuedes encontrarme en la parte derecha de cada página.\n</p><p>\n¡Vamos a empezar!\n</p>', '<h2>Hallo [person_name], welkom bij CoME!</h2>\n<p>\nMijn naam is Eva en ik wil u graag helpen om uw weg te vinden binnen CoME. Ook kan ik u helpen wanneer u vastloopt en zal ik soms even vragen hoe het met u gaat.\nU kunt mij hier vinden wanneer u mij nodig heeft.\n</p><p>\nLaten we gauw gaan beginnen!\n</p>', '<h2>Üdvözlöm a CoME oldalán kedves [person_name]!</h2>\n<p>\nAz én nevem Éva és szeretnék Önnek segíteni eligazodnia a CoME oldalán. Akkor is az Ön segítségére fogok sietni, ha valahol elakad és alkalmanként jelentkezni fogok automatikusan.\n</p><p>\nBármerre is jár a CoME oldalán, segítségért forduljon hozzám, a jobb szélen találhat meg.\n</p><p>\nÉs akkor kezdjük is az ismerkedést az oldalal!\n</p>'),
	(24, 50, 2, '<h2>Setting up your goals</h2>\n<p>\nGreat [person_name]!\n</p><p>\nAfter this introduction you can start by adding a new goal. The goals will help you to keep on going, and stay healthy.\n</p><p>\nGoals can be as small or big as you like, but let’s start with something small like ‘walk 30 minutes a day.’ You can create your own goal or select one of the predefined goals.\n</p><p>\nTo create a new goal please go to your Goals and click on “Add New Goal.”\n</p><p>\nStep 1/8\n</p>', '<h2>Establecer tus objetivos</h2>\n<p>\n¡Genial [person_name]!\n</p><p>\nDespués de esta introducción, puedes empezar estableciendo un objetivo. Los objetivos te ayudan a mantenerte activo y estar sano.\n</p><p>\nLos objetivos pueden ser tan sencillos o complejos como quieras, aunque es mejor empezar con algo sencillo como “caminar 30 min al día”. Puedes crear tu propio objetivo o seleccionar uno de los objetivos predefinidos.\n</p><p>\nPara crear un nuevo objetivo, ve a Objetivos y cliquea “Añadir Nuevo Objetivo”.\n</p><p>\nPaso 1/8\n</p>', '<h2>Doelen opstellen</h2>\n<p>\nGeweldig [person_name]!\n</p><p>\nNa deze introductie kunt u beginnen met een nieuw doel op te stellen. Doelen helpen u om niet op te geven en zorgen ervoor dat u gezond blijft.\n</p><p>\nDoelen kunnen zo groot of klein zijn als u zelf wilt. Zullen we eerst beginnen met iets kleins, zoals ‘30 minuten per dag wandelen.’? U kunt uw eigen doelen stellen of een vooraf ingevuld doel kiezen.\n</p><p>\nGa naar uw doelen om een nieuw doel aan te maken en kies “Nieuw doel toevoegen.”\n</p><p>\nStap 1/8\n</p>', '<h2>Céljai beállítása</h2>\n<p>\nKedves [person_name]!\n</p><p>\nEzután a rövid bemutató után kezdésképp érdemes volna beállítania egy célt. A megadott célok segíteni fogják Önt egészsége megőrzésében és abban, hogy motivált maradjon mindvégig.\nEzeknek a céloknak a volumene csak Öntől függ, de kezdjünk csak egy egyszerűbbel, pl. napi 30 perc séta. Saját célt is alkothat vagy választhat az oldal által előre megadottakból is.\n</p><p>\nÚj cél hozzáadásához kérjük menjen a Célok menüpontra, majd azon belül kattintson az “Alapvető vagy egyéni cél hozzáadása” gombra.\n</p><p>\nLépés 1/8\n</p>'),
	(25, 50, 3, '<h2>View your progress</h2>\n<p>\nDo you want to know how you are doing? You can get insight in your lifestyle by looking at your trends.\nBy connecting your device you can get insights in your physical activities, food intake, heart rate and sleeping patterns. By doing so you’ll be able to reach your goals more easily.\nHow great is that?\n</p><p>\nStep 2/8\n</p>', '<h2>Seguir tu progreso</h2>\n<p>\n¿Quieres saber cómo te encuentras? Puedes obtener información sobre tu estilo de vida y progreso en la sección Evolución.\nConectando tu disposivito puedes tener una mejor perspectiva de tu actividad física, consumo alimenticio, frecuencia cardíaca y patrones de sueño.\nHaciendo esto, serás capaz de alcanzar tus objetivos de forma más sencilla. ¿ No te parece fantástico?\n</p><p>\nPaso 2/8\n</p>', '<h2>Uw voortgang bekijken</h2>\n<p>\nWilt u weten hoe goed u het doet? U kunt inzicht krijgen in uw levensstijl door naar uw trends te kijken.\n</p><p>\nDoor uw activity tracker aan te sluiten kunt u inzicht krijgen in uw fysieke activiteit, dieet, hartslag en slaappatronen.\n</p><p>\nDoor dit te doen zult u gemakkelijker uw doelen bereiken. Klinkt dat niet geweldig?\n</p><p>\nStap 2/8\n</p>', '<h2>Nézze meg, hogyan teljesített</h2>\n<p>\nKíváncsi rá, hogy milyen fejlődésen ment keresztül? A “Tendenciák” menüpontba kattintva megtekintheti az egészségével kapcsolatos mérhető adatait.\nAzzal, hogy mérőeszközét (okosóra, okosmérleg, vérnyomásmérő, stb.) összeköti a CoME oldalával hozzáférhet fizikai aktivitásával kapcsolatos adataihoz, bejegyzett étkezési szokásaihoz, pulzusadataihoz, illetve alvási szokásaihoz.\nEnnek a folyamatnak köszönhetően könnyebben valósíthatja meg elérendő céljait. Ez egy nagyszerű lehetőség, nemde?!\n</p><p>\nLépés 2/8\n</p>'),
	(26, 50, 4, '<h2>Connecting your device</h2>\n<p>\nAre you ready to connect your device? In the top right corner click on the user icon to connect your device. Click on “wearable devices” open the page and to choose the right device. Are you ready? Then click on “connect your device”.\n</p><p>\nStep 3/8\n</p>', '<h2>Conectar tu dispositivo</h2>\n<p>\n¿Estás listo para conectar tu dispositivo? En la esquina de arriba a la derecha, pincha en el icono de usuario para conectar tu dispositivo. Pincha en “Dispositivos” para abrir la página adecuada y el dispositivo correcto. ¿Estás listo? Ahora pincha en “conectar dispositivo”.\n</p><p>\nPaso 3/8\n</p>', '<h2>Uw activity tracker aansluiten</h2>\n<p>\nBent u er klaar voor om uw activity tracker aan te sluiten? Rechtsboven in uw scherm kunt u klikken op het gebruikers icoontje. Click daarna op “activity trackers”\n</p><p>\nEr opent een pagina, kies de juiste activity tracker. Bent u er klaar voor? Klik dan op “sluit mijn activity tracker aan”\n</p><p>\nStap 3/8\n</p>', '<h2>Mérőeszköze csatlakoztatása</h2>\n<p>\nKészen áll arra, hogy csatlakoztassa mérőeszközét?\nAmennyiben igen, a jobb felső sarokban levő embert formáló ikonra kattintva találhatja a “Mérőeszközök” menüpontot. Erre kattintva válassza ki az Ön által csatlakoztatni kívánt készüléket. Ha ez megvan, akkor kattintson a “Kapcsolat létrehozása a mérőeszközével” gombra.\n</p><p>\nLépés 3/8\n</p>'),
	(27, 50, 5, '<h2>Sharing your feeling</h2>\n<p>\nYou’re doing wonderful so far!\n</p><p>\nYou can fill-out a report about yourself that can help you to get better insight in your lifestyle and it allows you to assess yourself so you can make changes when needed.\n</p><p>\nThe information from the self-report and also from your device will provide input for the trend analysis.\n</p><p>\nStep 4/8\n</p>', '<h2>Compartir cómo te sientes</h2>\n<p>\n¡Lo estás haciendo genial!\n</p><p>\nPuedes completar un informe sobre ti que te ayude a tener una mejor perspectiva de tu estilo de vida y decirte qué se puede mejorar en tu objetivo saludable.\n</p><p>\nLa información de estos test de auto-evaluación junto con tu dispositivo, te ayudarán a medir tu progreso.\n</p><p>\nPaso 4/8\n</p>', '<h2>Deel uw gevoelens</h2>\n<p>\nTot nu toe doet u het geweldig!\n</p><p>\nU kunt een vragenlijst invullen over uzelf om beter inzicht te krijgen in uw levensstijl en het creëert inzicht in hoe u zelf veranderingen kunt maken in uw leven.\n</p><p>\nDe informatie van de vragenlijst en van uw activity tracker zal genoeg zijn voor het creëren van een trendanalyse.\n</p><p>\nStap 4/8\n</p>', '<h2>Hangulata megosztása</h2>\n<p>\nEddig jól halad.\n</p><p>\nA CoME lehetőséget nyújt Önnek arra, hogy időről-időre rövid kérdésekre válaszolva jegyezhesse fel Ön az aktuális hangulatát.\n</p><p>\nEzeket az adatokat összevetve az aznapi, mérőeszköze által mért aktivitási/egészségügyi adatokkal képet kaphat arról, hogy min lenne érdemes változtatnia egészsége és boldogsága érdekében.\n</p><p>\nLépés 4/8\n</>'),
	(28, 50, 6, '<h2>Personalized reports from professionals</h2>\n<p>\nThe care professionals will support you in achieving a healthier lifestyle. You can check their latest updates in “Reports”.\n</p><p>\nStep 5/8\n</p>', '<h2>Informes personalizados de un profesional</h2>\n<p>\nUn equipo de profesionales te ayudarán a seguir un estilo de vida más saludable. Puedes revisar sus últimas recomendaciones en “Informes”\n</p><p>\nPaso 5/8\n</p>', '<h2>Rapportages van professionals</h2>\n<p>\nDe zorgprofessionals zullen u ondersteunen om een gezondere levensstijl te bereieken. U kunt hun laatste updates zien onder “Rapportages”.\n</p><p>\nStap 5/8\n</p>', '<h2>Személyre szabott jelentések a CoME orvosi csapatától</h2>\n<p>\nÖnt a CoME oldal használata során egészségügyi dolgozók fogják segíteni egészsége megőrzésében. Az “Információ” menüpontra kattintva találhatja az általuk elkészített jelentéseket.\n</p><p>\nLépés 5/8\n</p>'),
	(29, 50, 7, '<h2>Receiving support from your network</h2>\n<p>\nHere you can find your support network. You can add friends or family members that can support you in achieving your healthier lifestyle.\n</p><p>\nStep 6/8\n</p>', '<h2>Recibir apoyo de tus seres queridos</h2>\n<p>\nAquí puedes saber qué personas cercanas contribuyen a ayudarte en tu estilo de vida saludable.\nPuedes añadir tanto amigos como familiares para que te ayuden en este estilo de vida saludable.\n</p><p>\nPaso 6/8\n</p>', '<h2>Beginnen met CoME</h2>\n<p>\nNaasten\n</p><p>\nHier kunt u de ondersteuning binnen uw netwerk vinden. U kunt vrienden of familie toevoegen die u kunnen ondersteunen in het bereiken van een gezondere levensstijl.\n</p><p>\nStep 6/8\n</p>', '<h2>Az Ön támogatói hálózata</h2>\n<p>\nAz “Ismerősök” menüpont alatt találhatja az Ön családtagjainak, barátainak körét, akik regisztráltak a CoME odalra és Ön felvette velük a kapcsolatot az oldalon keresztül, hogy támogassák egészsége megóvásában.\n</p><p>\nLépés 6/8\n</p>'),
	(30, 50, 8, '<h2>Collaborating with your network</h2>\n<p>\nWell done.\n</p><p>\nYou can use MyGuardian to get in touch with the people around you.\n</p><p>\nMy Guardian allows you to get in touch with your loved ones. Feeling under the weather? They can give you a helping hand when needed. Or you can share the latest updates using “messages”.\n</p><p>\nLet''s have a look at My Guardian!\n</p><p>\nStep 7/8\n</p>', '<h2>Colaboración con tu red</h2>\n<p>\nBien hecho.\n</p><p>\nPuedes usar MyGuardian para contactar con las personas de tu entorno.\n</p><p>\nMyGuardian te permite estar en contacto con tus seres queridos.\n</p><p>\n¿Te sientes mal? Ellos siempre pueden prestarte una mano. O puedes compartir las últimas actualizaciones usando “mensajes”.\n</p><p>\n¡Vamo a echar un vistazo a MyGuardian!\n</p><p>\nPaso 7/8\n</p>', '<h2>Met uw netwerk samenwerken</h2>\n<p>\nGoed gedaan.\n</p><p>\nU kunt gebruik maken van MyGuardian en in contact te komen met de mensen die dichtbij u staan.\n</p><p>\nMyGuardian zorgt ervoor dat u in contact komt met uw naasten. Voelt u zich niet zo lekker? Zij kunnen u een handje helpen wanneer dat nodig is. Of u kunt hen een update geven door de “berichten” functie te gebruiken.\n</p><p>\nLaten we eens naar MyGuardian gaan kijken!\n</p><p>\nStap 7/8\n</p>', '<h2>Együttműködés az Ön hálózatával</h2>\n<p>\nNagyszerű.\n</p><p>\nA MyGuardian programját arra használhatja főként, hogy tartsa a kapcsolatot az Ön környezetében levő emberekkel.\n</p><p>\nBiztosítja, hogy szeretteivel könnyen tudjon kommunikálni. Vegyünk egy példát. Megfázott? Tudassa velük (és csak velük) és ők segíthetnek Önnek. Vagy megoszthatja a belső körével a legújabb történéseket az “üzenetek” menüpontban.\n</p><p>\nNézzük csak meg, hogy is működik a MyGuardian!\n</p><p>\nLépés 7/8\n</p>'),
	(31, 50, 9, '<h2>Almost done!</h2>\n<p>\nFantastic [person_name]!\n</p><p>\nYou’re ready to explore what CoMe has to offer on your own. If you want to change your settings you can go to your profile page.\n</p><p>\nThank you for your attention and I will see you soon!\n</p><p>\n*tip: Wish to relax? CoMe also offers various games!\n</p><p>\nStep 8/8\n</p>', '<h2>¡Ya casi está!</h2>\n<p>\n¡Fantástico [person_name]!\n</p><p>\nEstás preparado para echar un vistazo por ti mismo a lo que CoME te ofrece. Si quieres cambiar los ajustes, puedes ir a la página de perfil.\n</p><p>\n¡Gracias por tu atención! ¡Espero verte pronto!\n</p><p>\n*consejo: ¿Te quieres relajar?\n</p><p>\n¡CoME también te ofrece juegos!\n</p><p>\nPaso 8/8\n</p>', '<h2>Bijna klaar!</h2>\n<p>\nFantastisch [person_name]!\n</p><p>\nU bent er klaar voor om CoMe alleen verder te ontdekken. Als u aanpassingen wilt maken in uw instellingen kunt u dit doen door naar uw profielpagina te gaan.\n</p><p>\nDank u wel voor uw aandacht en ik zie u snel weer terug!\n</p><p>\n* tip: Wilt u even ontspannen? CoMe biedt ook verschillende spelletjes aan!\n</p><p>\nStap 8/8\n</p>', '<h2>Majdnem készen vagyunk!</h2>\n<p>\nMár könnyebben lesz képes ezentúl felfedezni a CoME oldalán, hogy az mit is nyújthat Önnek. Ha szeretné megváltoztatni a beállításait, akkor lépjen fel a profiloldalára.\n</p><p>\nKöszönöm az eddigi figyelmét, hamarosan találkozunk!\n</p><p>\nTipp: Ha szeretne kikapcsolódni, a CoME játékokat is biztosít Önnek, melyeket a jobb alsó sarokban találhat.\n</p><p>\nLépés 8/8\n</p>'),
	(32, 55, 1, 'Dear [person_name]\nI have just switched my language to match your preferences!', '[person_name], idioma cambiado', 'Beste [person_name], mijn taal is aangepast aan uw voorkeuren.', 'Kedves [person_name], innentől az Ön által preferált nyelven láthatja az üzeneteimet!'),
	(33, 56, 1, 'A new report with recommendations from professionals has just been sent to you! Do you want to look at it now?', 'Te hemos enviado un nuevo informe con recomendaciones de un profesional. ¿Quieres mirarlo ahora?', 'Een nieuw verslag met adviezen van professionals is naar u verstuurd! Wilt u er nu naar kijken?', 'A CoME orvosi csapatának tagjai épp most állítottak össze Önnek egy jelentést állapotáról ajánlásokkal. Szeretné megtekinteni most?'),
	(34, 57, 1, 'The professional supervising you has noticed some changes in your wellbeing. Maybe it would be good to contact your health professional.', 'El profesional que revisa tu progreso ha detectado algunos cambios en tu estado de salud. Quizás podrías contactar con un profesional de la salud.', 'Uw professional heeft veranderingen in uw gezondheid opgemerkt. Misschien is het een goed idee om contact op te nemen met uw professional.', 'A tanácsadója változást észlelt az Ön egészségi állapotában. Érdemes lehet felvennie a kapcsolatot orvosával/ápolójával.'),
	(35, 60, 1, 'Hi [person_name]! I am not noticing any activity from you. Are you wearing your smartwatch?\n\nI am unable to monitor your wellbeing. Please, could you put the smartwatch back on for me?”', '¡Hola [person_name]! Hace tiempo que no hay actividad. ¿Llevas puesto tu dispositivo?\n\nNo soy capaz de monitorizar tu estado de salud. Por favor, ¿ podrías ponerte de nuevo el reloj por mí?', 'Hallo [person_name]! Het valt mij op dat ik geen activiteiten van u binnenkrijg. Draagt u uw activity tracker?', 'Helló [person_name]! Egy ideje nem érzékeltem aktivitást az Ön részéről. Viseli az okosóráját?\n\nSajnos képtelen vagyok így felügyelni az egészségi állapotát. Kérem, kezdje el újra hordani az óráját, hogy segíthessek, rendben?'),
	(36, 61, 1, 'Hi [person_name], it’s been a great week. You have really helped me keep track your health and wellbeing. Thank you! Shall we have a look at all the things that happened?', 'Hola [person_name], ha sido una gran semana. Me has ayudado mucho a chequear tu estado de salud y bienestar. ¡Gracias! ¿Quieres echar un vistazo a todo lo que has logrado?', 'Hallo [person_name], deze week was echt geweldig. U heeft mij echt geholpen met het bijhouden van uw gezondheid. Dank u wel! Zullen we samen kijken naar hoe uw week is verlopen?', 'Helló [person_name], nagyszerű hetet tudhat maga mögött. Sokat segített abban, hogy megfelelően nyomon követhessem egészségi állapotát, köszönöm! Szeretné megtekinteni, hogy milyen eredményeket ért el?'),
	(37, 62, 1, 'Hi [person_name], we noticed you have been less active in the last couple of days. It would be great if we could turn this around. Would you like me to help you with that?”', 'Hola [person_name], hemos notado que has estado poco activo en los últimos días. Sería bueno que te reactivases. ¿Quisieras ayudarme con esto?', 'Hallo [person_name], het is ons opgevallen dat u minder actief bent geweest de laatste paar dagen. Het zou super zijn als we hier iets aan kunnen doen. Wilt u dat ik u hiebrij help?', 'Helló [person_name], látszólag csökkent az aktivitási szintje az elmúlt pár napban. Jó lenne, ha tudna ezen változtatni, nemde? Segíthetek Önnek ebben?'),
	(38, 63, 1, 'Hi [person_name], you’ve really been doing well lately, you have been quite active. Actually, you seem to be quite a champion!', 'Hola [person_name], lo has estado haciendo muy bien últimamente. ¡Eres todo un ganador!', 'Hallo [ person_name], u heeft het super goed gedaan de laatste tijd, u bent erg actief geweest. Of eigenlijk, u bent gewoon een kampioen!', 'Helló [person_name], az elmúlt időszakban meglehetősen aktív volt, jól haladt céljai felé. Így tovább, jó úton jár!'),
	(39, 64, 1, 'Hi [person_name],\nI see you haven’t filled in any goals. Goals will help you in achieving a healthier life style.\n\nDo you want me to help you set up a new goal? I know you will do amazing!', 'Hola [person_name], he visto que todavía no has añadido un objetivo. Los objetivos te ayudan a conseguir un estilo de vida más saludable.\n\n¿Quieres ayudarme y establecer un nuevo objetivo? ¡Seguro que lo haces genial!', 'Hallo [person_name], Ik zie dat u nog geen doelen heeft ingevuld. Doelen helpen u een gezondere levensstijl te bereiken.\n\nWilt u dat ik u help met het opzetten van een nieuw doel? Ik weet dat u het geweldig zult doen!', 'Helló [person_name], úgy látom, hogy nem adott meg elérendő célokat, pedig sokat segíthetnek abban, hogy egészségesebbé tudjon válni.\n\nSegíthetek Önnek, hogy beállítsunk együtt egy új célt? Biztos vagyok benne, hogy meg tudja valósítani!'),
	(40, 65, 1, 'Congratulations, [person_name]! You’ve achieved the [goal] goal. That’s amazing! Are you ready for a next challenge?', '¡Enhorabuena [person_name]! Has logrado tu objetivo [goal]. ¿Estás listo para el siguiente desafío?', 'Gefeliciteerd, [person_name]! U heeft het [goal] doel bereikt! Dat is echt geweldig! Bent u klaar voor de volgende uitdaging?', 'Gratulálok, [person_name]! Elérte a kitűzött célját, kiváló! Készen áll a következő kihívásra?'),
	(41, 66, 1, 'Good evening [person_name]. You’ve had a great day today, as you achieved your [goal]. I think your loved-ones would be very happy to see your achievement. Would you like to share it with them?', 'Buenas tardes [person_name]. Has tenido un día genial, a medida que alcanzabas tu objetivo [objetivo].\nCreo que tus seres queridos estarán muy felices de ver tus logros. ¿Te gustaría compartirlo con ellos?', 'Goedenavond [person_name]. U heeft vandaag een goede dag gehad, u heeft namelijk uw [goal] doel bereikt. Ik denk dat uw naasten het leuk zullen vinden om te zien dat u uw doel heeft bereikt. Zou u dit met hen willen delen?', 'Jó estét [person_name]!\nRemek napot tudhat maga mögött, elérte a célját: [goal].\nA szerettei biztos örömmel látnák ezt. Megosztja velük eredményét?'),
	(42, 67, 1, 'Good morning, [person_name]! I’m excited you’ll be starting your new goal [goal]. Perhaps your loved-ones will feel inspired by you. Would you like to share your new goal with your network?', '¡Buenos días, [person_name]! Estoy feliz de que hayas creado tu nuevo objetivo [goal]. Quizás tus seres queridos se animen a conseguirlo también. ¿Quieres comentar este nuevo objetivo con tus seres queridos?', 'Goedemorgen [person_name]! Ik ben verheugd over dat u vandaag begint met [goal] doel. Misschien zullen uw naasten door u geïnspireerd worden. Wilt u uw nieuwe doel met hen delen?', 'Jó reggelt [person_name]!\nIzgatottan várom, hogy elkezdje megvalósítani új célját: [goal]. Célja megosztásával a szeretteit is motiválhatja! Kívánja megosztani velük új célját?'),
	(43, 68, 1, 'Hi [senior name], I have noticed you have not yet created a goal. It would be great to see how you set a new goal.', 'Hola [senior_name], he notado que todavía no has creado un objetivo. Estaría genial ver cómo estableces tus objetivos.', 'Hallo [senior_name], het is mij opgevallen dat u nog geen doel heeft ingesteld. Het zou fijn zijn als u een nieuw doel op zou stellen.', 'Helló [senior_name], úgy látom, még nem jelölt ki célt magának. Sokat segíthetne egészsége megőrzésében, ha ezt megtenné.'),
	(44, 69, 1, 'Hi [senior name], you have not yet connected any devices. Do you need help to connect your device?', 'Hola [senior_name], todavá no has conectado ningún dispositivo. ¿Necesitas ayuda para conectar tu dispositivo?', 'Hallo [senior_name], u heeft vandaag geen verbinding gemaakt met uw activity tracker. Heeft u hulp nodig hulp nodig bij het instellen hiervan?', 'Helló [senior_name], még nem csatlakoztatott fiókjához egyetlen mérőeszközt sem. Szüksége van segítségre, hogy ezt megtegye?'),
	(45, 70, 1, 'Hi [senior name], on this page you can view your progress. Click on any theme you are interested in to see more details.', 'Hola [senior_name], en esta página puedes ver tu progreso. Pincha en cualquier tema en el que estés interesado para ver más detalles.', 'Hallo [senior_name], op deze pagina vindt u uw voortgang. Klik op een onderdeel die u interesseert om er meer informatie over te zien.', 'Helló [senior_name], ezen az oldalon tájékozódhat arról, miként alakultak egészségügyi paraméterei. Válassza ki bármelyik szegmenst, ami érdekli, hogy adatait részletesebben láthassa.'),
	(46, 80, 1, '[senior_name] has received a new report from a professional. Take a look!', '[senior_name] ha recibido un nuevo informe de un profesional. ¡Echa un vistazo!', '[senior_name] heeft een nieuw verslag van een professional ontvangen! Neem een kijkje!', '[senior_name] számára épp most küldött egy kiértékelést tanácsadója. Tekintse meg Ön is!'),
	(47, 82, 1, 'Hello [person_name], [senior_name] has reported that he/she has reached his/her [goal]. How about showing them how proud you are!', 'Hola [person_name], [senior_name] ha indicado que ha alcanzado su objetivo [objetivo]. ¡Muéstrales lo orgulloso que te sientes!', 'Hallo [person_name], [senior_name] heeft aangeven dat hij/zij haar/zijn [goal] doel heeft bereikt. Laat u even weten hoe trots u bent?', 'Helló [person_name], [senior_name] épp most valósította meg egy kitűzött célját. Tudassa vele, hogy büszke rá elért eredményéért!'),
	(48, 83, 1, 'Hi [informal_name], it’s been a great month.\n<p>\nWe have really helped keep track of [senior_name] health and wellbeing.\n</p><p>\nThank you! Shall we have a look at all the things that happened?\n<p>', 'Hola [informal_name], ha sido un mes genial.\n<p>\nNos has ayudado un montón a seguir al usuario [senior_name].\n</p><p>\n¡Gracias! ¿Quieres echar un vistazo a su perfil?\n</p>', 'Hallo [informal_name], het is een geweldige maand geweest.\n<p>\nWe hebben echt geholpen met het bijhouden van de gezondheid van [senior_name].\n</p><p>\nDank u wel voor uw inzet! Zullen we eens kijken naar alles wat is gebeurd afgelopen maand?\n</p>', 'Helló [informal_name], remek hónapon vagyunk túl.\n<p>\nSokat segítettünk [senior_name] egészségi állapotának nyomon követésében. Köszönet az Ön hozzájárulásáért! Szeretné megtekinteni, hogy mi történt ebben a periódusban?\n</p>'),
	(49, 84, 1, 'Hello [informal_name], [senior_name] has create a new goal [goal]. How about showing them how proud you are!', 'Hola [informal_name], [senior_name] ha creado un nuevo objetivo [objetivo]. ¡Muéstrale lo orgulloso que te sientes!', 'Hallo [informal_name], [senior_name] heeft een nieuw doel ingesteld! [goal]. Laat u even weten hoe trots u bent?', 'Helló [informal_name], [senior_name] épp most tűzött ki egy új célt magának. Tudassa vele, hogy büszke rá motiváltságáért!'),
	(50, 86, 1, 'Hi [informal_name]. [senior name] has told he/she is bored today.\n<p>\nHow about sending him/her a suggestion on how to turn their day around.\n</p><p>\nOr even better how about visiting them and doing a nice activity.\n</p>', 'Hola [informal_name]. [senior_name] dice que está un poco aburrido.\n<p>\n¿Por qué no le envías una sugerencia para mejorar su día?\n</p><p>\nO mejor aún, ¿por qué no la visitas y hacéis algo juntas?\n</p>', 'Hallo [informal_name]. [senior_name] heeft aangegeven dat hij/zij zich vandaag verveeld.\n<p>\nMisschien kunt u hem/haar een suggestie geven over hoe zij/hij hun dag kan veranderen.\n</p><p>\nOf nog beter: u kunt hem/haar bezoeken en samen iets leuks doen.\n</p>', 'Helló [informal_name], [senior_name] azt mondja, hogy ma unalmas napja van.\n<p>\nÉrdemes lenne egy javaslattal előállnia számára üzenetben, hogy hogyan dobhatja fel a napját.\n</p><p>\nHa személyesen is meg tudná látogatni, hogy közös programot csináljanak, az még jobb lenne!\n</p>'),
	(51, 88, 1, 'Good morning [senior_name], I hope you had a good night and are ready for a wonderful day. Do you want to tell me about your mood today? Or how are you felling today?', 'Buenos días , [senior_name]. Espero que hayas pasado una buena noche y que tengas un día genial. Dime cómo te sientes hoy.', 'Goedemorgen [senior_name], ik hoop dat u lekker heeft geslapen en klaar bent voor een geweldige dag. Wilt u mij vertellen over uw stemming vandaag? Of over hoe u zich vandaag voelt?', 'Jó reggelt [senior_name]! Remélem jól aludt éjszaka és készen áll egy csodás napra. Megosztaná velem, hogy milyen hangulatban van ma és hogy érzi magát?'),
	(52, 89, 1, 'Hello [senior_name], would you like to tell me how you’re doing now?', '¿Qué tal [senior_name]? ¿Cómo te encuentras hoy?', 'Hallo [senior_name], wilt u mij vertellen over hoe het met u gaat?', 'Helló [senior_name], megkérdezhetem, hogy hogy telik eddig a napja?'),
